css:
@-moz-document url-prefix("https://gyao.yahoo.co.jp/titles") {
/* 広告 */
.container-two-column-right,
.ad {
    display: none !important; }

/* 全体 */
.container-center-column .section.type-auto-fill,
.container-two-column {
    max-width: 100% !important; }

.program-list.type-grid .program-list-item.has-favorite {
    border: 1px solid #eee !important; }

/* ウォッチリストボタン */
.program-list-item.has-favorite {
    padding-bottom: 0 !important; }

.program-list-item-buttons {
    top: 0 !important;
    right: auto !important;
    bottom: auto !important; }

/* 紹介文 */
.program-list-item-titles {
    overflow: auto !important; }

.program-list-item-titles .program-list-item-link {
    height: auto !important;
    max-height: 10em !important; }

.program-list-item-titles .program-list-item-description {
    display: block !important;
    max-height: max-content !important;
    max-width: 100% !important; }

@media(min-width:1740px) {
    .program-list.type-grid .program-list-items {
        grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr; } }
}
