css:
@-moz-document url-prefix("https://gyao.yahoo.co.jp/titles") {
.container-two-column-right {
    display: none !important;
}
.container-center-column .section.type-auto-fill,
.container-two-column {
    max-width: 100% !important;
}
.button-favorite:not(.type-small) {
    max-width: 100% !important;
    overflow: hidden !important;
}
.program-list .program-list-item-buttons {
    margin: 0 !important;
}
.program-list-item-sub-text:last-child {
    max-height: initial !important;
}
.program-list .program-list-item-titles {
    padding-right: 10px !important;
    max-height: 10em !important;
    overflow: hidden !important;
}
.program-list .program-list-item-titles:hover {
    overflow: auto !important;
}
.program-list .program-list-item {
    display: flex !important;
    border-left: 1px solid #eee !important;
    border-right: 1px solid #eee !important;
}
.program-list .program-list-items {
    display: flex !important;
    flex-wrap: wrap !important;
}
.program-list .program-list-item-content {
    flex-direction: column !important;
}
@media (min-width: 301px) and (max-width:500px) {
    .program-list .program-list-item {
        width: calc(100% / 2) !important;
    }
}
@media (min-width: 501px) and (max-width:700px) {
    .program-list .program-list-item {
        width: calc(100% / 3) !important;
    }
}
@media (min-width: 701px) and (max-width:900px) {
    .program-list .program-list-item {
        width: calc(100% / 4) !important;
    }
}
@media (min-width: 901px) {
    .program-list .program-list-item {
        width: calc(100% / 5) !important;
        max-width: 300px !important;
    }
}
}
