css:
/*elmahatta dark theme - made by Kareem Mohammed - https://www.facebook.com/kareemmoegy*/

@-moz-document url(https://elmahatta.com/) {
	/*header*/
.content-column .single-container:last-child, .site-header .header-style-8 .full-width .content-wrap {
    background-color: #424242;
}
.site-header .header-style-8 .boxed .header-inner, .site-header .header-style-8 .full-width, .site-header .header-style-8 .full-width > .bs-pinning-wrapper > .content-wrap .pinned {
    border-bottom-color: #757575;
}
/*.header------------------*/

/*main color*/
 .site-header .header-style-8 .header-style-8 .main-menu>li>a, .listing-item .listing-mg-type-1 .title .title a .post-url .post-url, .listing-item-blog-5> .item-inner> .title a, .listing-item-blog-5 .has-post-thumbnail .post-summary, ul .menu .footer-menu li > a, .rltdpstsplgn_posts_title a:link, .rltdpstsplgn_posts_title a, .listing-item-grid-1 .title a, .listing-item-grid-1 .post-summary, .listing-item-text-2 .title, .bs-newsletter-pack .bsnp-t1 .bsnp-title, .post-template-13 .single-post-title, .post .entry-content .single-post-content:last-child, .heading-typo, .site-header .site-branding .logo, h1, h2, h3, h4, h5, h6, .bs-post-pagination::before, .more-stories-title, .listing-item-text-2 .title a, .heading-typo .post-author-url, .disclaimer-note p {
    color:#e0e0e0;
}
/*sub color*/
 .post-tp-13-header .single-post-title+.post-subtitle, .bf-breadcrumb .bf-breadcrumb-item a, .bf-breadcrumb .bf-breadcrumb-item span, .post-meta .post-author, .post-meta .single-post-meta .post-author-name, .post-meta .single-post-meta .post-author-a, .post-meta .single-post-meta .time, .post-meta .single-post-meta .views, .entry-content .gallery-caption, .entry-content .wp-caption-text, .entry-content figcaption, .author-links .author-social-icons .post-author-bio {
    color: #bdbdbd;
}
.post-meta .single-post-meta>a+span:before, .post-meta .single-post-meta>span+a:before, .post-meta .single-post-meta>span+span:before {
    background: #bdbdbd;
}
pre, .entry-content .bs-post-pagination .pages, .pagination .bs-numbered-pagination span, .editors-table p {
    background-color: #212121;
    border: 1px solid #757575;
    color: #e0e0e0;
}
.more-stories {
    background-color: #212121;
    border: 1px solid #757575;
}
.comment-ajaxified-placeholder {
    background-color: #212121;
    color: #e0e0e0;
}
.single-container>.post-author, .editors-table {
    border: 1px solid #757575;
}
.more-stories-title {
    border: 1px solid #757575;
}
.listing-blog> .listing-item-blog {
    border-top: 1px solid #757575;
}
.section-heading .sh-t7:before {
    background: #75757580;
}
.rltdpstsplgn_posts_title a:link, .rltdpstsplgn_posts_title a:visited, .rltdpstsplgn_posts_title a, .listing-item-text-2 .item-inner {
    border-right: 3px solid #75757580;
}
.listing-item-grid-1 .post-summary {
    border-bottom: 1px solid #75757580;
}
.bs-newsletter-pack .bsnp-t1 .bsnp-s7 {
    border: 1px solid #75757580;
}
.bs-pagination .bs-ajax-pagination .more_btn .btn-bs-pagination {
    box-shadow: 0 2px 5px -1px #75757580;
    border-color: #75757580;
}
.bs-pagination .bs-ajax-pagination .more_btn .btn-bs-pagination span {
    color: #e0e0e0;
}
.section-heading .sh-t7> .h-text:last-child:first-child {
    background: #757575;
    color: #e0e0e0;
}
}
