css:
@-moz-document  regexp('(http(s)?://)?(www.)?baidu\.com/(s|b).*')
 {
    body {
	overflow-x: hidden;
	min-width: 600px;
}
/*关键词高亮*/
 em, font[color="#CC0000"] {
	background-color: #f8f8f8 !important;
	color: #C00 !important;
	text-decoration: none !important;
	border-bottom: 1px solid #C00 !important;
}
.wrapper_l .s_form {
	width: 760px !important;
	margin: 0 auto !important;
	transform: translatex(-40px);
}
.s_tab, .nums, .search_tool_conter, #rs, .to_tieba, .to_zhidao_bottom {
	margin: 0 auto !important;
}
.headBlock {
	margin-left: 0;
}
.headBlock > div {
	margin: auto;
}
.head_nums_cont_inner > div {
	width: 600px;
}
.s_ipt {
   margin:5px 8px;
}
#page, #s_tab {
	padding-left: 0px !important;
	text-align: center !important;
}
#wrapper_wrapper {
	background: #f8f8f8;
}
#container {
	width: 100%;
}
#content_right {
	display: none;
}
#content_left {
	padding: 0;
	width: 96%;
	padding-left: 2%;
	display: block;
	column-gap: 0;
	-moz-column-gap: 0;
	-webkit-column-gap: 0;
}
.rrecom-container, .xpath-log:not(.c-container) {
	transform: scale(0);
}
@media(min-width: 1201px) and (max-width: 1700px) {
	#content_left {
		column-count: 2;
		-moz-column-count: 2;
		-webkit-column-count: 2;
	}
}
@media(min-width: 1701px) and (max-width: 2200px) {
	#content_left {
		padding-left: 3%;
		column-count: 3;
		-moz-column-count: 3;
		-webkit-column-count: 3;
	}
}
@media(min-width: 2201px) {
	#content_left {
		padding-left: 4%;
		column-count: 4;
		-moz-column-count: 4;
		-webkit-column-count: 4;
	}
}
#content_left > div:not([class^="result"]) {
	position: absolute;
	margin-top: -1em;
	transform: scale(0);
}
#content_left > div[class^="result"] {
	display: inline-block;
	width: 91%;
	padding:16px 18px;
	margin: 1em;
	word-break: normal;
	word-wrap: normal;
	position: relative;
	z-index: 1;
	border-radius: 10px;
	background-color: white;
	overflow: hidden;
	transform: translate(0);
	transition: transform .5s;
}
#content_left > div[class^="result"]:before {
	content: attr(id);
	z-index: -10;
	color: #eee;
	font-size: 4em;
	position: absolute;
	text-align: right;
	width: 96%;
	line-height: 1em;
	top: 6px;
}
#content_left > div[class^="result"]:after {
	content:"";
	position: absolute;
	left: 0;
	bottom: 0;
	width: 3px;
	height: 100%;
	background-color: #eee;
	transition: all .3s;
}
#content_left > div[class^="result"] .sitelink {
	word-wrap: break-word;
}
#content_left > div[class^="result"]:hover {
	transform: translate(0, -3px);
	box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}
#content_left > div[class^="result"]:hover:before {
	color:deeppink;
}
#content_left > div[class^="result"]:hover:after {
	background-color: deeppink;
}
#content_left .result-op > div {
	width: 540px;
}
.c-border {
	border: 0;
	box-shadow: none;
}
.to_tieba {
	display: none;
}
.hint_common_restop {
	display: none;
}
.c-container .t, .c-default .t {
	margin-bottom: 10px;
}
#rs {
	width: 100%;
	text-align: center;
	padding: 0;
	margin: 0;
	margin-top: 10px;
}
#rs .tt, #rs table, #rs tr, #rs td, #rs th {
	display: inline;
	padding: 0 5px;
}
#page {
	margin: 15px 0 !important;
	text-align: centre !important;
	padding: 0 !important;
}
#page .fk {
	display: none !important;
}
#page > a, #page > strong {
	height: auto !important;
   background: transparent;
}
#foot {
	margin-top: 35px !important;
	text-align: center !important;
}
#foot #help {
	float: none !important;
	padding: 0 !important;
}
.f13 {
	margin-top:10px;
}
.soutu-btn {
	background:url(https://ss1.bdstatic.com/5eN1bjq8AAUYm2zgoY3K/r/www/cache/static/protocol/https/soutu/img/camera_new_5606e8f.png) no-repeat;
}
#result_logo>img {
	content:url(https://www.baidu.com/img/baidu_resultlogo@2.png);
}
#form>.bdsug {
	border-radius: 1em;
	overflow: hidden;
	padding: 10px 0;
	backdrop-filter: blur(30px);
	background-color: rgba(255, 255, 255, 0.9);
}
#form>.bdsug>.bdsug-feedback-wrap {
	display:none;
}
#form>.bdsug li {
	line-height: 30px;
}
#head .bdsug {
	top: 40px;
}
div#head {
	min-height:0px!important;
	-webkit-backdrop-filter: blur(20px);
	backdrop-filter: blur(20px);
	background-color: rgba(255, 255, 255, 0.6);
	border-radius: 0px 0 25px 25px;
}
.s_ipt_wr {
	border-radius:.8em 0 0 .8em
}
#su {
	border-radius: 0 .8em .8em 0;
}
div#head_wrapper {
	min-height:0px!important
}
div#bottom_container {
	display:none!important
}
a.s-treasure {
	display:none!important
}
a.mnav, a.s-skin, a.s-msg, span.user-name, span.setting-text {
	text-decoration:none!important
}
a:hover {
	text-decoration:none!important
}
* {
	text-decoration:none!important
}
.t {
	position: relative;
}
 }
@-moz-document url-prefix("http://baidu.com"), url-prefix("http://www.baidu.com"), url-prefix("https://www.baidu.com"), url-prefix("https://baidu.com")
{
/*去除贴吧知道*/
  #rs, .to_tieba, .to_zhidao_bottom
  {
    display: none;
  }
}
