css:
@-moz-document domain("doc.qt.io") {
	
	.navigationbar > ul:nth-child(3) {
		border: 3px solid #17a81a;
		border-right: none;
		background: #f3f3f4;
		border-radius: 0.5em 0 0 0.5em;
		font-weight: bold;
		font-size: 200%;

		padding: .5em;

		position: fixed;
		right: 0;
		
		z-index: 999;
		text-align: right;
	}

	.navigationbar > ul:nth-child(3) > li {
		display: none;
	}

	.navigationbar > ul:nth-child(3) > li:nth-child(2) {
		display: inline-block;
	}

	.navigationbar > ul:nth-child(3) > li > a {
		font-size: 24pt;
	}

	.navigationbar > ul:nth-child(3) > li::after {
		content: '';
	}

	.navigationbar:hover > ul:nth-child(3) > li {
		display: block;
		padding-right: .75em;
		line-height: 110%;
	}

	.navigationbar:hover > ul:nth-child(3) {
		padding-right: 0;
	}

}
