css:
@-moz-document url-prefix("https://translate.google.com/translate") {
.gb_f {
    height: 30px !important;
    line-height: 28px !important;
}
#gba, .gb_rf { height: 30px !important; }
#gbq2 { padding-top: 0px !important; }
#gt-appbar {
    border-bottom: none !important;
    height: 29px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}
#contentframe { top: 60px !important; }
}

@-moz-document regexp("https?://books.google.(?:com|([a-z]{2})(.[a-z]{2})?)/books.*") {
.gb_f {
    height: 30px !important;
    line-height: 28px !important;
}
#gba, .gb_rf { height: 30px !important; }
#gbq2 { padding-top: 0px !important; }
.kd-appbar {
    height: 29px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}
#volume-main { border-top: none !important; }

.kd-appbar .kd-appname { 
    margin: 0 5px 0 5px !important;
    width: 90px !important;
}
.jfk-button { margin-right: 0px !important; }
}
