css:
@-moz-document domain("www.vitalbmx.com") {
body, #user_menu, .sp-nav-bar{
    background: #303030 !important;
}
p, div.box.gray h5{
    color: #cfcfcf;
}
h1, .first p, .content .sidebar .left h3, .titleContent, body, .cmmnt_count, .sidebar.community.left h3, .poll_results table tbody tr th{
    color: #fff !important;
}
.body strong{
    color: #fff;
}
blockquote p, fieldset blockquote, .user_widget p, .post_preview p, .post_preview strong, .post_preview li, .comment .body .text p, .comment_vote .text p, .unreg-users span, .fileName, .data{
    color: #000 !important;
}
div.first, tr.sticked, #more_product_info, .b-product-reviews-detail__top, .setup-checks-specs th, .setup-check-specs th, #comment_list, div.box.gray, .item-inner, .inner, .item, .footer_big_feature, .footerProducts.affiliateContainer, .multiSidebar .affiliateContainer, .spb-nav-bar, th, .footer_spotlights, .cf_wrapper, .cmmnt_li.loaded{
    background: #424242 !important;
}
#container, #footer{
    background: #212121 !important;
}
span.timestamp, .item.product.updatedProductFilter ul li a, div.item .inner ul li label, div.item.style1 div.inner p, .comment_count, .price strong, .submenu .nav ul li, dl dt, dl dd{
    color: #9f9f9f !important;
}

.bmx a.button.inverse{
    color: #07b;
    background: #212121
}

div.box.forum_post.first{
    border: none;
}

.truncFade{
    background: none !important;
}

table.forum tbody tr{
    border-bottom: 1px solid #929292;
}

blockquote div p strong, div.body blockquote{
    color: #000 !important;
}
}
