css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.redissueforum.co.uk") {
body {
background: #fff !important;
color: #000 !important;
font-size: 10px !important;
margin: 0;
padding: 8px;
}

a {
color: #666 !important;
}

table {
background-color: #fff !important;
border-width: 0 !important;
color: #000 !important;
}

div {
color:#000 !important;
}

strong {
background-color: #efefef;
color: #000;
font-weight: normal;
}


table[name="header"], 
table#subnav,
td.alt1 img,
td.alt2 img,
td.tcat,
td.thead,
td.tfoot,
td.smallfont img,
td.vbmenu_control,
img[alt~="Avatar"],
img.inlineimg[alt~="Go"] {
display: none;
}

/* hide the left column */
table[width="156"], 
table[width="156"] table {
width: 1px !important;
}

td.alt2, 
td.vbmenu_control {
background-color: #fff !important; 
color:#000 !important;
}

td.alt1Active a {
border-bottom: 1px dotted #efefef !important; 
text-decoration: none !important
}

td.thead {
background-color:#fff !important
}


img[title="Big Grin"],
img[title="Angry"],
img[title="Unhappy"],
img[title="Talking"],
img[title="Smile"],
img[title="Wink"],
img[title="Thumbs up"],
img[title="Thumbs down"],
img[src="images/buttons/quote.gif"],
img[src="images/buttons/lastpost.gif"] {
display: inline !important;
}

img[src="images/buttons/lastpost.gif"] {
height: 8px;
width: 8px;
}

a.bigusername {
font: normal 12px verdana !important
}

/* reply and new thread pages */
td.panelsurround {
background-color:#efefef !important
}

div.panel {
background-color:#f8f8f8 !important
}


  
}
