css:
@-moz-document domain(hardstyle.com) {
/*Dark Stuff*/


/*transparent overlay*/
.overlay--lighter, .overlay {
    background-color: #2f2f2fd6
}

/*text*/
/*text general*/
h1, h2, h3, h4,  p, span {
    color: white !important;
    background-image: none !important;
}
/*blocks & codeboxes*/
blockquote {
    border-color: #d5d5d5 !important;
    color: #d5d5d5 !important;
}


/*bottom banner delete*/
.u-backgroundWhite {
    display: none;
}
/*light green*/
.u-textColorLighter {
    color: #73d085c9 !important;

}
/*stars*/
.uiScale .ui-caption, .u-textColorNormal, .u-fillTransparentBlackDarker, .y {
    fill: #00b4ff !important;
}
/*Transparency hole*/
.metabar-block div {
    background-color: transparent !important;
}

/*light-dark*/
body, html, article, .cardChromeless, .screenContent, .canvas-renderer, .u-backgroundColorWhite {
    background-color: #2F3031 !important;
    color: #d5d5d5;
}

#columns {
    background-color: black;
}

/*links*/
a, a:hover, a b {
    color: #00b4ff !important;
}

#columns p, b {
    color: white !important;
}

.navbar-inverse .navbar-nav > li > a, .button.blue {
    color: white;
}

#column-middle .box, #top-downloads .bundle-options span, div.content, #top-downloads li {
    background-color: #2c2c36;
    color: white;
}

#top-downloads li>a>b {
    color: #00b4ff;
}
}
