@-moz-document domain("zportal.nl") {
@keyframes rainbowcycle {
    0% {color: #ff0000;}
    5% {color: #ff2200;}
    10% {color: #ff4400;}
    15% {color: #ff6600;}
    20% {color: #ff8800;}
    25% {color: #ffaa00;}
    30% {color: #ffcc00;}
    35% {color: #ffdd00;}
    40% {color: #ffff00;}
    45% {color: #aaff00;}
    50% {color: #88ff00;}
    55% {color: #44ff00;}
    60% {color: #00ff00;}
    65% {color: #00bb44;}
    70% {color: #008888;}
    75% {color: #0044bb;}
    80% {color: #0000ff;}
    85% {color: #2200dd;}
    90% {color: #4400cc;}
    95% {color: #6600aa;}
    100% {color: #880088;}
}
@keyframes backgroundcycle {
    0% {background-color: #ff0000;}
    5% {background-color: #ff2200;}
    10% {background-color: #ff4400;}
    15% {background-color: #ff6600;}
    20% {background-color: #ff8800;}
    25% {background-color: #ffaa00;}
    30% {background-color: #ffcc00;}
    35% {background-color: #ffdd00;}
    40% {background-color: #ffff00;}
    45% {background-color: #aaff00;}
    50% {background-color: #88ff00;}
    55% {background-color: #44ff00;}
    60% {background-color: #00ff00;}
    65% {background-color: #00bb44;}
    70% {background-color: #008888;}
    75% {background-color: #0044bb;}
    80% {background-color: #0000ff;}
    85% {background-color: #2200dd;}
    90% {background-color: #4400cc;}
    95% {background-color: #6600aa;}
    100% {background-color: #880088;}
}
.zermeloSideMenu {
    background-image: url("https://ak3.picdn.net/shutterstock/videos/25458653/thumb/1.jpg");
    filter: brightness(1.2);
}
.zermeloTitleSmall {
    color: #000000;
}
.zermeloSideMenuItemSelected {
    background-color: rgba(0,0,0,0);
}
.zermeloSideMenuItemSelected * {
    filter: brightness(0);
}
.zermeloSideMenuItemSelected:hover {
    background-color: #888;
}
.zermeloSideMenuItem {
    background-color: rgba(0,0,0,0);
}
.zermeloSideMenuItem:hover {
    background-color: #888;
}
.zermeloTopBar {
    filter: saturate(0);
}
a, * {
    animation: rainbowcycle;
    animation-duration: 6s;
    animation-iteration-count: infinite;
}
.zermeloModule * {
    animation: none;
}
.head {
    background-image: url("https://i.imgur.com/KyNKoPF.png");
    background-repeat: no-repeat;
    background-size: auto;
}
.head * {
    filter: saturate(0) brightness(6);
}
}
