@-moz-document domain("vapewild.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("vapewild.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.navPages-container nav ul.navPages-list>li>a,
.account-orderStatus-label,
.btn,
.cart-header-item,
.datepicker-day-name,
.form-label--inlineSmall small,
.optimizedCheckout-form-input,
.optimizedCheckout-headingSecondary,
.page-sidebar .side-module-heading,
.productView .productView-brand,
ul.navBar-section li.navBar-item,
ul.navBar-section li.navBar-item a
{
	text-transform: capitalize!important;
}

.navPages-container nav ul.navPages-list>li>a,
#footerSubcribeForm>form .form-field,
#quickSearch>form .form-field,
.desc-line p,
.haloProductsByCategory .category-products,
.haloProductsByCategory .category-title,
.haloProductsByCategory::before,
.module-wrapper.productList .productBlockContainer .prod-item .card-figure .prod-desc a.btnWL span,
.optimizedCheckout-orderSummary,
.prod-item .prod-image .actions a+a,
.prod-item .prod-image .actions span.warp-actions,
.warpBlockBanner .haloProductsByCategory .category-products,
.warpBlockBanner .haloProductsByCategory .category-title,
.warpBlockBanner .haloProductsByCategory::before,
:focus,
[data-wishlist-add]>button span,
fieldset,
ul.tabs li.tab,
ul.tabs li.tab.is-active a,
ul.tabs li.tab:first-child.is-active:before,
ul.tabs li.tab:last-child.is-active:before
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

table {
    border-collapse: separate!important;
    border-spacing: 38px!important;
}

.featured-brands .banner-item {
    z-index: 0!important;
}

/*___ Fonts ________________________________________________________________________*/

.brandGrid .brand .card-title,
.btn,
.button,
.cart-header-item,
.cart-item-brand,
.cart-total-label,
.cart-total-value,
.descLeft h4,
.form-label,
.form-label--inlineSmall small,
.navPages-container li.hasMegamenu>.subMegaMenu .cateArea>ul>li>a,
.optimizedCheckout-buttonPrimary,
.optimizedCheckout-contentPrimary,
.optimizedCheckout-contentSecondary,
.previewCart .productView-info .productView-info-name,
.prod-item .prod-desc h4.prod-name,
.prod-item article.card,
.productView .productView-brand,
.yotpo .yotpo-comment .yotpo-main .content-title,
.yotpo .yotpo-onsite-upload .yotpo-main .content-title,
.yotpo .yotpo-question .yotpo-main .content-title,
.yotpo .yotpo-review .yotpo-main .content-title,
a,
body,
h1, h2, h3, h4, h5, h6,
p,
ul.all-categories-list a,
ul.all-categories-list>li #navPages-level-3>li>a,
ul.navBar-section li.navBar-item a
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.module-wrapper.productList .productBlockContainer .prod-item .card-figure .prod-desc a.btnWL i {
    font-size: 24px!important;
}

.cart-item-name,
.cart-header-item {
	font-size: 21px!important;
}

.address-title,
.card-title,
.navPage-subMenu-action.navPages-action.has-subMenu,
.prod-item .prod-desc h4.prod-name,
.product-options,
.yotpo .yotpo-comment .yotpo-main .content-title,
.yotpo .yotpo-onsite-upload .yotpo-main .content-title,
.yotpo .yotpo-question .yotpo-main .content-title,
.yotpo .yotpo-review .yotpo-main .content-title
{
	font-size: 19px!important;
}

.navPages-container nav ul.navPages-list>li>a,
.address-details--postal,
.account-orderStatus-label,
.compareTable-product .price-section,
.form-label,
.navPages-container li.hasMegamenu>.subMegaMenu .cateArea>ul>li>a,
.optimizedCheckout-form-input,
.panel--address,
p
{
	font-size: 17px!important;
}

.category-description p,
.datepicker-day-name,
.form-select--date,
.form-select--small,
.navPages-container li.hasMegamenu>.subMegaMenu .cateArea ul li a span,
.productView .productView-brand,
body,
ul.all-categories-list>li #navPages-level-3>li>a
{
	font-size: 15px!important;
}

.form-label--inlineSmall small {
	font-size: 14px!important;
	line-height: 19px!important;
}

.address-details--postal {
	line-height: 24px!important;
}

.form-label {
	font-weight: normal!important;
}

/*___ Remove Items _________________________________________________________________*/

.banner-item a::before,
.header-middle-right:before,
ul.navBar-section li.navBar-item.is-active:before,
ul.navBar-section li.navBar-item.is-active:after {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.prod-item .prod-image .actions {
    top: -5%!important;
}

.module-wrapper:not(.productList) .prod-item a.btnATC i.icon-cart {
    margin-right: 15px!important;
}

/*___ Margins ______________________________________________________________________*/

.category-description,
.layout-cart,
.layout-cart.is-sticky,
.layout-main,
.module-wrapper .productBlockContainer.columns-4 .prod-item,
aside.page-sidebar.col-sm-3,
aside.page-sidebar.col-sm-3~main.page-content.col-sm-9
{
	margin: 12px!important;
}

ul.navBar-section li.navBar-item {
	margin: 3px 6px!important;
}

.cart-header-link .icon {
    margin: -2px 21px 0 0!important;
}

/*___ Paddings _____________________________________________________________________*/

.bgr-cart,
.category-description,
.layout-cart,
.layout-cart.is-sticky,
.layout-main,
.module-wrapper .productBlockContainer.columns-4 .prod-item,
aside.page-sidebar.col-sm-3~main.page-content.col-sm-9
{
	padding: 12px!important;
}

.datepicker-day.is-selected::before,
.datepicker-day.is-selected:hover::before
{
	padding: 14px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

.owl-carousel .owl-stage-outer {
    height: 33rem!important;
}

/*___ Widths _______________________________________________________________________*/

.container_page,
.form-select--date,
.form-select--small,
.layout,
[data-wishlist-add]>button
{
	max-width: none!important;
}

.form-ccFields-field--ccCvv, .form-field--ccExpiry {
    min-width: 8.69231rem!important;
}

selector {
	width: auto!important;
}

.card-figure,
.prod-item article.card {
    width: 100%!important;
}

.container,
.layout,
.module-wrapper .productBlockContainer.columns-4 .prod-item.fadeIn
{
	width: 98%!important;
}

aside.page-sidebar.col-sm-3~main.page-content.col-sm-9 {
    width: calc(95% - 240px)!important;
}

[data-wishlist-add]>button {
    width: 83%!important;
}

#product-listing-container,
.BottomContent
{
    width: 82%!important;
}

/*
.owl-carousel .owl-stage-outer .card-figure {
    width: 74%!important;
}
*/
.layout-main {
    width: 57%!important;
}

.module-wrapper .productBlockContainer.columns-4 .prod-item,
aside.page-sidebar.col-sm-3
{
    width: 15%!important;
}


.module-wrapper .productBlockContainer.columns-5 .prod-item {
    width: 12%!important;
}

.form-select--date,
.form-select--small
{
    width: 30rem!important;
}






}

@-moz-document domain("vapewild.com") {
/*__________________________________________________________________________________*/

td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.cart-item-brand,
.datepicker-day.is-selected,
.productView .productView-brand,
[data-wishlist-add]>button:hover span,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
ul.navBar-section li.navBar-item:hover a
{
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#invitation_message_text,
.FDA-Warning.page-top,
.datepicker-day,
.dropdown-menu-item a,
.form-label[for*="attribute_"],
.module-wrapper.productList .productBlockContainer .prod-item .card-figure .prod-desc a.btnWL span,
.optimizedCheckout-contentSecondary,
.previewCart .productView-info .productView-info-value,
.shippingOption-desc,
.yotpo .yotpo-comment .yotpo-main .content-review,
.yotpo .yotpo-onsite-upload .yotpo-main .content-review,
.yotpo .yotpo-question .yotpo-main .content-review,
.yotpo .yotpo-review .yotpo-main .content-review,
[data-wishlist-add]>button span,
[data-wishlist-add]>button:hover i.fa,
body,
dd,
li,
p,
ul.navBar-section li.navBar-item a,
ul.navBar-section li.navBar-item
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/


.cart-header-item,
.datepicker-month,
.navPage-subMenu-action.navPages-action.has-subMenu,
.paymentProviderHeader-name,
.yotpo .yotpo-comment .yotpo-main .content-title,
.yotpo .yotpo-onsite-upload .yotpo-main .content-title,
.yotpo .yotpo-question .yotpo-main .content-title,
.yotpo .yotpo-review .yotpo-main .content-title,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.datepicker-day-name,
.form-legend,
.previewCart .productView-info .productView-info-name,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.address-label,
.cart-total-label, 
.definitionList-key,
.optimizedCheckout-contentPrimary,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.account-product-detail span,
a,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.cart-item-value,
.cart-priceItem-value,
.cart-total-value,
.compareTable-product .price-section,
.previewCartCheckout-price,
.prod-item .prod-desc .prod-price,
.productView-price,
.shippingOption-price
{
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.datepicker-day.datepicker-day-prev-month,
.form-label--inlineSmall small {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.button,
.view-mode-btn,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Code _________________________________________________________________________*/

.token.punctuation {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #ff6cbe!important;
}
























}

@-moz-document domain("vapewild.com") {

/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray ______________________________________________________________*/

#WhyUsSection,
#breadcrumbs-wrapper,
#footer-wrapper,
.header,
.optimizedCheckout-header
{
	background-color: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

#footerSubcribeForm>form .form-field,
#quickSearch>form .form-field #search_category,
.cart-header,
.desc-line p,
.dropdown-menu-item:focus,
.dropdown-menu-item:hover,
.haloProductsByCategory .category-products,
.haloProductsByCategory .category-title,
.heroCarousel,
.page-sidebar .accordion-content,
.page-sidebar .side-module-heading,
.previewCart #mayAlsoLike>h3,
.prod-item .prod-image .actions a,
.product-pagination.top,
.warpBlockBanner .haloProductsByCategory .category-products,
ul.navBar-section li.navBar-item a,
ul.navBar-section li.navBar-item,
ul.tabs li.tab.is-active a,
ul.tabs li.tab.is-active,
ul.tabs li.tab.is-active:after
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.featured-brands .content-brands,
.account-orderStatus-label,
.brg-content,
.card-figure,
.optimizedCheckout-orderSummary,
.productView .productView-description,
.productView .productViewTop,
.sidebarBlock
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__________________________________________________________________________________*/

.modal-background {
    background: rgba(0, 0, 0, 0.93)!important;
}

/*__ Blue __________________________________________________________________________*/

.btn.btn-oranges,
.btn.btn-primary,
[data-wishlist-add]>button
{
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn.btn-oranges:hover,
.btn.btn-primary:hover,
[data-wishlist-add]>button:hover
{
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn.btn-oranges:active,
.btn.btn-primary:active,
[data-wishlist-add]>button:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

.featured-brands .brands_btn a {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.featured-brands .brands_btn a:hover {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

.featured-brands .brands_btn a:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Purple ________________________________________________________________________*/

#top-cart,
.btn.btn-checkout,
[data-cart-item-add]>button#form-action-addToCart
{
	background-image: linear-gradient(to bottom right, #230d7e, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#top-cart:hover,
.btn.btn-checkout:hover,
[data-cart-item-add]>button#form-action-addToCart:hover
{
	background-image: linear-gradient(to bottom right, #341e8f, #000)!important;
	color: #ff0!important;
}

#top-cart:active,
.btn.btn-checkout:active,
[data-cart-item-add]>button#form-action-addToCart:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#previewModal:before,
.BottomContent>div,
.bgr-cart,
.category-description,
.haloProductsByCategory::before,
.layout-cart,
.layout-cart.is-sticky,
.layout-main,
.warpBlockBanner .haloProductsByCategory::before,
.wrap-account,
aside.page-sidebar.col-sm-3,
aside.page-sidebar.col-sm-3~main.page-content.col-sm-9
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#previewModal:before:hover,
.BottomContent>div:hover,
.bgr-cart:hover,
.category-description:hover,
.haloProductsByCategory::before:hover,
.layout-cart.is-sticky:hover,
.layout-cart:hover,
.layout-main:hover,
.warpBlockBanner .haloProductsByCategory::before:hover,
.wrap-account:hover,
aside.page-sidebar.col-sm-3:hover,
aside.page-sidebar.col-sm-3~main.page-content.col-sm-9:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}



/*___ Plain ________________________________________________________________________*/

