#invitation_message_text,
.brands,
.compareTable-product,
.datepicker,
.dropdown-menu,
.modal,
.module-wrapper:not(.productList) .prod-item,
.navPages-container nav ul.navPages-list>li>.navPage-subMenu,
tr.cart-item
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.navPages-container nav ul.navPages-list>li>.navPage-subMenu li>.navPage-childList:hover,
#invitation_message_text:hover,
.brands:hover,
.compareTable-product:hover,
.datepicker:hover,
.dropdown-menu:hover,
.modal:hover,
.module-wrapper:not(.productList) .prod-item:hover,
.navPages-container nav ul.navPages-list>li>.navPage-subMenu:hover,
tr.cart-item:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.soldOut-badge,
.compareTable-row:hover,
.FDA-Warning.page-top {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

.form-checklist-item:hover,
.form-checklist-item--selected {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
}

/*__ Blue Gradient _________________________________________________________________*/

#NeedHelp {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
