css:
@-moz-document domain("hquties.win") {
body {
    color: #c0c0c0;
    background: #333;
}

.bg-white {
    background-color: #222 !important;
}

.list-group-item {
    background-color: #222;
}

.swal2-popup {
    background-color: #222;
}

.swal2-title {
    color: #fff
}

.light-font {
    color: #c0c0c0;
}

h4.mb-3 {
    color: #9d36b0;
}

.swal2-content {
    color: #c0c0c0;
}

.bg-content-e {
    background-color: #333;
}

.text-dark {
    color: #fff !important;
}

a.text-dark:focus, a.text-dark:hover {
    color: #c0c0c0 !important;
}

.text-muted {
    color: #c0c0c0 !important;
}

.card {
    background-color: #222;
}

.dropdown-header {
    color: #c0c0c0;
}

.dropdown-item {
    color: #c0c0c0;
}

.border-bottom {
    border-bottom: 1px solid #37343a !important;
}

.dropdown-item:focus, .dropdown-item:hover {
    color: #c0c0c0;
    text-decoration: none;
    background-color: #222;
}

.dropdown-menu {
    background-color: #37343a;
}

.img-thumbnail {
    background-color: #222;
    border: 1px solid #37343a;
}

.alert-warning {
    color: #fff;
    background-color: #222;
    border-color: #34373c;
}

.close:focus, .close:hover {
    color: #fff;
}
.close {
    color: #c0c0c0 !important;
}
   
.bg-content-a {
    background-color: #333;
}

.cute-color, .landing-content > div > h4 {
    color: #1573dd;
}

a {
    color: #dbdbdb;
}

a:hover {
    color: #fff;
}
}
