css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("torrentz.eu") {
* {
   color:rgb(120,120,120)!important;}

* a:hover,
* span:hover {
   color:orange!important;}


body {
   margin:3px auto 3px auto!important;
   width:800px!important;
   background-color:rgb(213,213,213)!important;
   background-image:url("http://img21.imageshack.us/img21/3194/fadep.gif")!important;
   background-repeat:repeat-x!important;
   border:1px solid rgb(180,180,180)!important;}

.top {
   background-image:url(http://img142.imageshack.us/img142/494/headerfill.png)!important;
   background-repeat:repeat-x!important;
   background-position:top left!important;
   height:39px!important;
   border-top:13px solid rgb(250,250,250)!important;
   padding:3px 3px 0 3px!important;}

.top li,
.top ul {
   height:auto!important;
   background-color:transparent!important;
   background-image:none!important;
   border:0!important;
   padding:0 3px 0 3px!important;
   margin-top:-15px!important;}

.top li:hover {
   background-color:rgb(250,250,250)!important;}

.top li>a:hover {
   border:0!important;
   color:orange!important;}

.top ul>li>a {
   height:auto!important;  
   font-size:15px!important;
   padding:3px!important;
   background-color:transparent!important;
   border:0!important;
   width:auto!important;
   color:white!important;}

.top>h1>a {
   opacity:0!important;
   height:auto!important;  
   font-size:30px!important;
   padding-left:2px!important;
   background-color:transparent!important;
   border:0!important;
   width:auto!important;
   color:white!important;}

.top>h1 {
   display: block;
   padding: 43px 0 0 0;
   overflow: hidden;
   background-image: url(http://img509.imageshack.us/img509/2969/torrentzlogo.png);
   background-repeat: no-repeat;
   height:43px !important;
   width:500px!important;
}

.search {
   padding:15px 1px 1px 1px!important;
   margin:0!important;
   background-image:none!important;
   background-color:rgb(250,250,250)!important;}

.search div, 
.search fieldset {
   background-image:none!important;
   background-color:rgb(250,250,250)!important;
   border:0!important;}

#thesearchbutton {
   background-color:rgb(250,250,250)!important;
   font-family:'Calibri'!important;
   border:1px solid gray!important;
   margin-left:5px!important;
   width:auto!important;
   color:rgb(100,100,100)!important;}

.button div {
   color:gray!important;
   text-align:left!important;
   padding-left:3px!important;}

.box {
   width:790px!important;
   padding:0!important;
   margin:2px!important;}

#thesearchbox {
   border:1px solid gray!important;
   width:784px!important;
   padding:0!important;
   margin:2px!important;
   font-size:13px!important;
   color:rgb(100,100,100)!important;}

.button {
   padding:5px!important;
   margin:0!important;
   height:30px!important;
   background-color:transparent!important;
   background-image:none!important;
   color:white!important;}

.button input {
   font-size:12px!important;
   padding:2px 10px 2px 10px!important;
   margin:2px!important;
   height:auto!important;
   width:auto!important;
   background-color:rgb(240,240,240)!important;
   border:1px solid gray!important;
   color:rgb(70,70,70)!important;}

.cloud {
   font-size:6px!important;
   background-color:rgb(250,250,250)!important;
   padding:15px 5px 10px 5px!important;
   margin:0!important;
   height:auto!important;}

.cloud a {
   padding:2px!important;  
   color:gray!important;
   text-decoration:none!important;}

.cloud a:hover {
   color:orange!important;}

body>div[style*="20px"] {
   background-color:rgb(250,250,250)!important;
   padding:10px 0 25px 0!important;
   margin:0!important;
   color:gray!important;}

.results,
.download,
.trackers,
.files,
.comments,
.profile,
.feedback {
   margin:0!important;}

.profile {
   background-color:white!important;
   width:auto!important;}

.profile textarea {
   width:695px!important;}

.download,
.files,
.comments,
.trackers,
.feedback {
   border-top:15px solid rgb(250,250,250)!important;}

.results dl,
.download dl,
.trackers dl {
   background-color:rgb(250,250,250)!important;
   background-image:none!important;
   margin:0!important;
   padding:2px 5px 2px 5px!important;}

.trackers dl {
   padding:0 5px 0 5px!important;}

.results dl:hover,
.download dl:hover,
.trackers dl:hover {
   background-color:rgb(240,240,240)!important;}

.results a:hover,
.download a:hover,
.trackers a:hover {
   color:orange!important;
   text-decoration:none!important;}

.results dl dt {
   width:500px!important;
   background-position:left!important;
   padding-left:20px!important;}

.download dl {
   width:auto!important;
   padding:0 10px 0 2px!important;
   height:25px!important;
   margin:0!important;}

.download dl a:hover,
.download dl a {
   width:auto!important;
}

.download dl * {
   font-size:12px!important;
   text-decoration:none!important;}

.download .n {
   margin-left:-50px!important;
   width:400px!important;}

.download dl a:hover {
   background-color:rgb(240,240,240)!important;}

.results h2,
.download h2,
.trackers h2,
.files h2,
.files div,
.comments h2,
.feedback h2 {
   background-color:rgb(250,250,250)!important;
   background-image:url("http://img142.imageshack.us/img142/494/headerfill.png")!important;
   background-repeat:repeat-x!important;
   border-bottom:4px solid rgb(250,250,250)!important;
   padding:5px!important;
   color:white!important;
   font-size:11px!important;}

.download p,
.files p,
.trackers p {
   background-color:rgb(250,250,250)!important;
   padding:5px!important;
   color:gray!important;
   font-size:11px!important;
   border-top:0!important;
   border-top:10px solid rgb(250,250,250)!important;}

.info {
   background-color:rgb(250,250,250)!important;
   color:gray!important;
   padding:2px 5px 2px 35px!important; 
   margin:0!important;
   font-size:12px!important;
   font-weight:bolder!important;
   border-top:20px solid rgb(250,250,250)!important;
   border-right:0!important;
   border-bottom:20px solid rgb(250,250,250)!important;
   border-left:0!important;}


.profile,
.user ul,
.files ul,
.comment,
.feedback p {
   background-color: rgb(250,250,250) !important;}

.head {
   border-top:1px solid rgb(222, 228, 235)!important;
   border-right:1px solid rgb(222, 228, 235)!important;
   border-bottom:0!important;
   border-left:1px solid rgb(222, 228, 235)!important;}

.com {
   background-color:white!important;
   border-top:0!important;
   border-right:1px solid rgb(222, 228, 235)!important;
   border-bottom:1px solid rgb(222, 228, 235)!important;
   border-left:1px solid rgb(222, 228, 235)!important;}

.user {
   margin:0!important;}

.profile fieldset {
   padding:10px!important;}

.profile legend,
.profile label,
.profile input,
.profile p {
   font-size:12px!important;
   padding:2px 10px 2px 10px!important;}

.help {
   background-color:rgb(250,250,250)!important;
   border-top:20px solid rgb(250,250,250)!important;
   border-bottom:20px solid rgb(250,250,250)!important;
   margin-top:0px!important;
   margin-bottom:0px!important;
   padding:10px!important;}

.user h2 {
   background-image:url("http://img142.imageshack.us/img142/494/headerfill.png")!important;
   background-repeat:repeat-x!important;
   margin:0!important;
   padding:5px!important;
   color:white!important;
   font-size:12px!important;
   border-top:20px solid rgb(250,250,250)!important;
   border-right:0!important;
   border-bottom:20px solid rgb(250,250,250)!important;
   border-left:0!important;}

.user div {
   padding:20px!important;}

div[style*="728px"],
div[style*="300px"] {
   display:none!important;}

.download h2 *,
.download div,
.trackers div,
.user a {
   color:white!important;
   font-size:12px!important;
   padding:5px!important;}

.results div,
.results div * {
   color:white!important;
   font-size:12px!important;
   padding:9px 3px 1px 3px!important;}

.results p {
   background-image:url("http://img142.imageshack.us/img142/494/headerfill.png")!important;
   background-repeat:repeat-x!important;
   font-size:12px!important;
   border-top:20px solid rgb(250,250,250)!important;
   height:35px!important;}

.results h2 a {
   color:red!important;}

.results p * {
   background-image:none!important;
   color:black!important;
   border-color:grey!important;}

.results h2 a:hover {
   color:orange!important;}

.footer {
   padding:15px 0 5px 0!important;
   width:100%!important;
   margin:0!important;
   background-color:rgb(250,250,250)!important;
   color:gray!important;}

body>div[style*="1000px"] {
   display:none!important;
   }

}
