@-moz-document domain("mysku.ru") {
#body, .menu {
    color: #BABDB6 !important;
    background: #2E3436 !important;
}
#wrapper {
    max-width:71%;
}
.topic .content h2, .topic-content h2, .topic .content h3, .topic-content h3, .topic .content h4, .topic-content h4, .topic .content h5, .topic-content h5, .topic .content h6, .topic-content h6 {
    color: #367d60 !important;
}
.menu li a {
