    color: #BABDB6 !important;
    border-bottom: solid 1px #c8c8c8 !important;
}
#content-container {
    color: #BABDB6 !important;
    background-color: #2E3436 !important;
}
#content {
    background: #2E3436 !important;
    color: #BABDB6 !important;
}
.article-table.article-table-column tbody td:first-child {
    background-color: none !important;
}
.poll-header span {
    color: #ad7 !important;
}

.white-back {
    background: #2e3436 !important;
}
.topic .topic-title {
    color: #252525 !important;
}
.topic .topic-title a, .topic .topic-title h1 {
	color: #ad7 !important;
    border-bottom: none !important;
}

.border a, a {
    color: #7bbb92 !important;
}
.block {
    background: #2E3436 !important;
}
.block.stream {
    background: #2E3436 !important;
}
