}
.spoiler {
    border: solid 1px #BABDB6 !important;
    display: block !important;
    background: url(http://assets.mysku-st.ru/templates/skin/mysku.v3/images/discount_title_bg.png) repeat-x scroll 0 38px #2E3436 !important;
}
.spoiler .spoiler-title {
    color: #7cb8bb !important;
    background: #2E3436 !important;
}
.spoiler .spoiler-title span {
