    color: #ad7 !important;
    border-bottom: 0px;
}
.profile-blog a:hover {
    color: #ad7;
    border-bottom: 1px solid #becae3;
}
.sku-info .sku-info-line li a {
    color: #ad5 !important;
    border-bottom: solid 0px !important;
}
/* Purchase button */
 .purchase_button span.arrow {
    background: url(http://assets.mysku-st.ru/templates/skin/mysku.v3/images/purchase_button_arrow.png) 50% 50% #2e3436 no-repeat !important;
}
.purchase_button span.text {
    color: #7cb8bb !important;
}
.purchase_button span.number {
    color: #7cb8bb !important;
}
.purchase_button span.dropdwn_list span {
    color: #7cb8bb !important;
    background: #2e3436 !important;
}
.purchase_button span.arrow:hover, .purchase_button span.text:hover {
    background-color: #2e5436 !important;
}
.purchase_button span.dropdwn_list span:hover {
    color: #7cb8bb !important;
    background: #2e5436 !important;
}
/* Like button */
 .ilike_button span.text {
    color: #7cb8bb !important;
}
.ilike_button .like_btn:hover {
    background: #2e5436 !important;
}
.ilike_button span.finger_down {
    background: url(http://assets.mysku-st.ru/templates/skin/mysku.v3/images/ilike_button_finger_down.png) #2e3436 50% 50% no-repeat !important;
}
.ilike_button span.finger_down:hover {
