@-moz-document url-prefix("https://aternos.org/start/") {
/* Main */
::-webkit-scrollbar {
	display: none !important;
}

body {
	background-image: url(https://images.pexels.com/photos/566169/pexels-photo-566169.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940) !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    animation: backgrounds 120s infinite !important;
}

@keyframes backgrounds
    {
      0%   {background-image: url(https://images.pexels.com/photos/1563332/pexels-photo-1563332.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      20%  {background-image: url(https://images.pexels.com/photos/1563332/pexels-photo-1563332.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      25%  {background-image: url(https://images.pexels.com/photos/164357/pexels-photo-164357.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      45%  {background-image: url(https://images.pexels.com/photos/164357/pexels-photo-164357.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      50%  {background-image: url(https://images.pexels.com/photos/1260584/pexels-photo-1260584.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      70%  {background-image: url(https://images.pexels.com/photos/1260584/pexels-photo-1260584.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      75%  {background-image: url(https://images.pexels.com/photos/748626/pexels-photo-748626.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      95%  {background-image: url(https://images.pexels.com/photos/748626/pexels-photo-748626.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
      100%  {background-image: url(https://images.pexels.com/photos/1563332/pexels-photo-1563332.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940);}
    }

@keyframes logs
    {
      0%   {background: none;}
      50%   {background: red;}
      100%   {background: none;}
    }

.alert-container main {
	background: rgba(0, 0, 0, 0.6) !important;
    border: 2px solid #1fd78d !important;
    color: white !important;
}

/* Header */
.header {
	background: rgba(0, 0, 0, 0.6) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/d385u2n.png);
}

.friend-access-dropdown {
	border: none !important;
    background: rgba(0, 0, 0, 0.8) !important;
}

.friend-access-switch-user {
	color: #2b87d3 !important;
    font-weight: bold !important;
    padding-top: 2.5px !important;
    padding-bottom: 2.5px !important;
}

.friend-access-switch {
	border-bottom: 1px solid #a0a0a0 !important;
}

/* Sidebar */
.sidebar {
	background: rgba(0, 0, 0, 0.5) !important;
}

/* Navigation */
.navigation {
	background: rgba(0, 0, 0, 0.6) !important;
}

.navigation a {
	border: none !important;
}

.navigation a:hover {
	background: none !important;
    color: #a0a0a0 !important;
}

.navigation a .navigation-log-problems-badge {
	animation: logs 2.5s infinite !important;
    color: black !important;
}

.navigation .item:after {
	color: #a0a0a0 !important;
}

.navigation a .fas {
	font-weight: 10 !important;
}

.navigation-toggle {
	font-weight: 10 !important;
}

.navigation-toggle:hover {
	color: #a0a0a0 !important;
}

/* Content */
.content {
	background: rgba(0, 0, 0, 0.5) !important;
}

.start-activity-list a {
	background: rgba(43, 135, 211, 0.5) !important;
}

.start-activity-list a:hover {
	background: rgba(35, 119, 188, 0.5) !important;
}

.start-activity-list a .fas, .start-activity-list a .far, .start-activity-list a .fab {
	font-weight: 10 !important;
    color: black !important;
}

.start-activity-help .start-activity {
	background: rgba(31, 215, 141, 0.5) !important;
}

.start-activity-help .start-activity:hover {
	background: rgba(24, 173, 111, 0.5) !important;
}

.tutorials a {
	background: rgba(0, 0, 0, 0.5) !important;
    -webkit-border-radius: 5px !important;
    border: 1px solid black !important;
    box-shadow: 0 0 5px black !important;
}

.tutorials a:hover {
	background: rgba(0, 0, 0, 0.7) !important;
}


/* Colors:

*/
}

