css:
@-moz-document domain('roblox.com')
{
  .icon-robux
  {
    background-position: 0 -140px !important;
  }

  .icon-nav-robux
  {
    background-position: 0 -56px !important;
  }

  .text-robux-lg
  {
    color: #CC9E71 !important;
  }

  button:hover .icon-nav-robux,
a:hover .icon-nav-robux
  {
    background-position: -28px -56px !important;
  }

  .item-cards-stackable.item-card-price.icon-robux
  {
    background-position: 0 -80px !important;
  }

  .text-robux
  {
    color: #CC9E71 !important;
  }

  .icon-robux-gray
  {
    background-position: 0 -60px !important;
    filter: grayscale(1) !important;
    -moz-filter: grayscale(1) !important;
    -webkit-filter: grayscale(1) !important;
  }

  .robux-grid.cell-content.robux-title span[class^="icon"]
  {
    background-position: 0 -190px !important;
  }

  .account-field-email-verified-text
  {
    color: #02b757 !important;
  }

  .icon-robux-16x16
  {
    background-position: 0 -80px !important;
  }

  .icon-robux-gray-16x16
  {
    background-position: 0 -80px !important;
    filter: grayscale(1) !important;
    -moz-filter: grayscale(1) !important;
    -webkit-filter: grayscale(1) !important;
  }

  span.robux, div.robux
  {
    background: url('https://www.roblox.com/images/Tickets.png') no-repeat 0 1px !important;
    color: #A61 !important;
    padding: 0 0 2px 20px !important;
    font-weight: bold !important;
    font-size: 12px !important;
  }

  .gotham-font.icon-robux-16x16
  {
    background-position: 0 -90px !important;
  }

  .icon-robux-white
  {
    background-image: url(https://images.rbxcdn.com/bf31e6fe81fbb16c33758b84bf962f64-navigation_10022018.svg) !important;
    background-position: 0 -72px !important;
  }
}
