css:
@-moz-document regexp("https:\\/\\/www.kamesame.com.*") {
	#app.kamesame {
    	background-color: rgb(67, 72, 88);
    	color: white;
	}
	#app.kamesame .level-bar {
		background-color: rgb(67, 72, 88);
    	color: white;
	}
	#app.kamesame nav {
		background-color: rgb(80, 85, 101);
	}
	
	#app.kamesame nav li a {
		color: white;
	}
	
	#app.kamesame nav li a:visited {
	    color: white;
	}
	
	#app.kamesame #account label {
	 	color: white;
	}
	
	#app.kamesame #study .outcome {
	 	background-color: rgb(67, 72, 88);
	    color: white;
	}
  #app.kamesame #home .stats h4 {
           background: rgb(67, 72, 88) none repeat scroll 0 0;
           color: white;
        }
        #app.kamesame #reviews .stats h4 {
           background: rgb(67, 72, 88) none repeat scroll 0 0;
           color: white;
        }
        #app.kamesame #reviews .review-bucket h4 {
           background: rgb(67, 72, 88) none repeat scroll 0 0;
           color: white;
        }
}
