css:
@-moz-document domain(developer.mozilla.org) {
	html {
		color: #ccc;
		background-color: #252a33 !important;
	}
	main{
		background-color: #252a33;
	}
	a {
		color: #7996cf;
	}
	input, select, textarea {
		color: #ccc;
		background-color: #1b1d23;
		box-shadow: 0 0 0.3em #181a1f inset;
	}
	html,
	div,
	nav {
		scrollbar-color: #3D4350 #21252B;
	}
	#main-header {
		background: #21252B;
		color: #ccc
	}
	.toc{
		background: #21252B;
	}
	.nav-main-item > a:focus, .nav-main-item > a:hover,
	.document-head,
	.login > a:focus, .login > a:hover{
		background-color: #252a33;
		color: #ccc;
	}
	html:not(.no-js) .nav-main-item > a{
		color: #ccc;
	}
	.quick-links .toggle summary{
		color: #4d78cc	
	}
	.submenu{
		border-top: 5px solid #555;
		background-color: #252a33;
		box-shadow: 0 1px 5px #5C6370, 0 5px 25px #000;
	}
	.deprecated, .deprecatedBadge, .indicator-danger, .nonStandard, .nonstandardBadge, .obsolete, .obsoleteBadge, .unimplemented, .unimplementedBadge, .warning{
		background: rgba(255, 231, 232, 0.1);
	}
	.draft, .indicator-warning, .note, .notice, .prefixBox, .privilegedBadge, .renamed, .secureContexts, .todo, .translationInProgress, .warning.warning-review{
		background: rgba(255, 243, 212, 0.1);	
	}
	.text-content table.bc-table{
		background-color: rgba(212, 221, 228, 0.15);
	}
	.newsletter-box{
		background-color: rgba(255, 255, 255, 0.1);	
	}
	input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="text"], input[type="url"], textarea {
		color: #ccc;
		background-color: #1b1d23;
	}
	.button, button, input[type="button"], input[type="submit"]{
		background-color: #21252B;
		color: #4d78cc;
		box-shadow: 0 0 0.2em inset;
	}
	ul.tags li{
		background-color: #21252B;
		border-color: rgba(206, 233, 249, 0.5);
	}
	ul.tags li a{
		color: #639d8a	
	}
	:not(pre) > * > code, :not(pre) > code{
		background-color: rgba(220, 220, 220, 0.1);	
	}
	.text-content table.standard-table:not(.learn-box) td.header, .text-content table.standard-table:not(.learn-box) th,
	.text-content table.standard-table:not(.learn-box):not(.nostripe) tr:nth-child(2n+1) td,
	.text-content table.standard-table:not(.learn-box) td{
		background: rgba(212, 221, 228, 0.1);
		border-color: rgba(255, 255, 255, 0.3);
	}
	pre.syntaxbox, pre.twopartsyntaxbox{
		color: #bbb;
		background-color: #181d26;	
	}
	.text-content code[class*="language-"], .text-content pre[class*="language-"]{
		color: #bbb;
	}
	.text-content pre[class*="language-"], pre.syntaxbox, pre.twopartsyntaxbox{
		background: #181d26;	
	}
	article a.new {
		color: #a74848;
	}
	.text-content .card-grid > li,
	.communitybox,
	.activity thead,
	.activity tbody tr:nth-child(2n),
	.revision-list li:nth-child(2n+1){
		background-color: rgba(0, 0, 0, 0.3)	
	}
	.bc-table .bc-history{
		background: #21252B;
		color: #ccc;
	}
	.bc-supports-no a, .bc-supports-partial a, .bc-supports-unknown a, .bc-supports-yes a{
		color: #7996cf;
	}
	.wiki-main-content,
	.user-head{
		background-color: transparent;
	}
	table.diff{
		color: #333	
	}
	.text-content .cssprop, .text-content .htmlelt, .text-content .learn-box, .text-content .properties, .text-content .summary-box-events, .text-content .summary-box-js{
		color: #bbb;
		background-color: #181d26;	
	}
}
