/* code by Micah Bucy (aka eternalsword) */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mapquest.com/settings") {
  #titlebar {
    width: 731px !important;
    min-width: 0px !important;
  }
  #settings {
    width: 734px !important;
  }
}

@-moz-document domain("mapquest.com") {
  div[class^="offers"], 
  div[id^="dualpromos"], 
  td#rightcolumn,
  .f11,
  .offerwrap,
  #mqaolhat {
    display: none !important;
  }

  #mqcontent > div,
  .mapgrinder,
  #mqcontent > div > div,
  #mqcontent > div > div > div div, 
  #mqheader > div:first-child,
  #mapsummary div,
  #dirsummary div,
  #formpage, 
  #formpage div,
  .l,
  .l div { 
    margin-left: auto !important;
    margin-right: auto !important;
  }
  #mqheader > div:first-child {
    width: 598px !important;
  }
  #dirsummary {
    width: 480px !important;
  }
  #rightpadding {
    display: none !important;
  }
  #mqfooter,
  .disclaimer,
  #mqpage > div:first-child { 
    text-align: center !important; 
  }
  .disclaimer { 
    width: 500px !important; 
  }
  #home #mqpage .l,
  .dirwrapper,
  #maps #tofrom .dirwrapper {
    float: none !important;
  }
}
