css:
body {
    font-family: 'Proxima-nova', sans-serif;
    font-weight: 500;
    font-size: 15px;
    -webkit-font-smoothing: antialiased;
    background-color: hsla(0, 0%, 100%, 0.2);
    color: hsla(0, 0%, 100%, 0.2);
    display: table;
    width: 100%;
    height: 100%;
    margin: 0 auto;
    line-height: 1.5;
    -webkit-tap-highlight-color: hsla(0, 0%, 100%, 0);
    -ms-text-size-adjust: 100%;
    -webkit-text-size-adjust: 100%;
    background: -moz-linear-gradient(125deg, #283048, #859398);
    background: -o-linear-gradient(125deg, #283048, #859398);
    background: -ms-linear-gradient(125deg, #283048, #859398);
    background: #283048;
    background: -webkit-linear-gradient(125deg, #283048, #859398);
    background: linear-gradient(125deg, #ff0000, #000fff);
}
.button {
    font-family: 'Proxima-nova', sans-serif;
    background: hsla(0, 0%, 100%, 0.1);
    color: #ffffff;
    border-radius: 2px;
    border: 1px solid hsla(0, 0%, 100%, 0.3);
    padding: 17px 20px;
    font-weight: 600;
    font-size: 15px;
    display: inline-block;
    cursor: pointer;
    outline: 0;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    box-sizing: border-box;
    -webkit-transition: all 0.1s ease-out;
    -moz-transition: all 0.1s ease-out;
    -ms-transition: all 0.1s ease-out;
    -o-transition: all 0.1s ease-out;
    transition: all 0.1s ease-out;
}
/* i really want this to be global */
.button.green {
    background: hsla(0, 0%, 100%, 0.1);
    color: hsla(0, 0%, 100%, 0.3);
    border: none;
}
.ui-slider-handle {
    background: #000;
    position: absolute;
    width: 32px;
    height: 32px;
    top: 50%;
    display: block;
    transform: translate(-50%, -50%);
    border-radius: 100px;
    z-index: 10;
    background: hsla(0, 0%, 100%, 0.2);
    cursor: move;
    cursor: grab;
    cursor: -moz-grab;
    cursor: -webkit-grab;
    box-shadow: -2px -2px 6px 2px rgba(0, 0, 0, 0.1);
    transition: width 0.1s;
}
element.style {
}
.button.green {
    background: hsla(0, 0%, 100%, 0.1);
    color: hsla(0, 0%, 100%, 0.3);
    border: none;
}
::-webkit-scrollbar-thumb {
    background: hsla(0, 0%, 100%, 0.2);
}
::-webkit-scrollbar-track {
    background: hsla(0, 0%, 100%, 0.2);
}
.ui-slider .ui-slider-range {
    height: 4px;
    background: hsla(0, 0%, 100%, 0.3);
    border-radius: 20px;
    position: absolute;
    transform: translateY(-px);
}
.alert.green {
    background: hsla(0, 0%, 100%, 0.3);
}
.alert {
    color: #ffffff;
    margin: 20px 25px 0;
    border-radius: 3px;
    padding: 12px 25px;
    box-shadow: 0 3px 12px hsla(0, 0%, 0%, 0.05);
    max-width: 600px;
    font-weight: 600;
    font-size: 15px;
    text-align: center;
    cursor: pointer;
    white-space: pre-wrap;
    transform-origin: center center;
    animation-name: zoomIn;
    animation-duration: .2s;
    transition: all .2s;
}
.button.red {
    background: hsla(0, 0%, 0%, 0.05);
    border: none;
    color: hsla(0, 0%, 100%, 0.3);
}
.user-menu .text {
    transition: 0.2s;
    margin-left: 8px;
    color: #fff0;
    font-size: 15px;
    font-weight: 500;
    line-height: 70px;
    cursor: pointer;
    -webkit-transform: rotateY(180deg);
}
element.style {
}

.box {
    display: inline-block;
    width: 100%;
    box-sizing: border-box;
    margin-bottom: 20px;
    background: hsla(0, 0%, 0%, 0.28);
    padding: 20px 20px 25px 20px;
    border-radius: 6px;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-shadow: inset 0px 0px 20px 20px #00000045;
    margin-bottom: 30px;
}
.table tbody tr {
    transition: .1s background-color;
    color: hsla(0, 0%, 100%, 0.2);
    cursor: pointer;
}
.table tr td .sub {
    font-size: 13px;
    font-weight: 500;
    color: hsla(0, 0%, 100%, 0.3);
}
.page-head .sub-text {
    font-size: 15px;
    font-weight: 500;
    color: hsla(0, 0%, 100%, 0.3);
}
.list__label .side__btn {
    float: right;
