.label {
    cursor: pointer;
    font-size: 15px;
    font-weight: 600;
    text-align: left;
    margin-bottom: 5px;
    color: hsla(0, 0%, 100%, 0.3);
}
.select .selector a {
    display: block;
    width: 100%;
    border: 1px solid hsla(0, 0%, 100%, 0.1);
    font-size: 15px;
    font-weight: 600;
    padding: 12px;
    color: hsla(0, 0%, 100%, 0.3);
    border-radius: 3px;
    text-decoration: none;
    text-align: left;
}
input[type=text], input[type=email], input[type=password], input[type=search], input[type=number], select, textarea {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    font-family: 'Proxima-nova';
    border: 1px solid hsla(0, 0%, 100%, 0.1);
    background-color: transparent;
    font-size: 15px;
    border-radius: 3px;
    padding: 1rem;
    color: hsla(0, 0%, 100%, 0.3);
    font-weight: 600;
    border-radius: 3px;
    outline: 0;
    display: inline-block;
    text-align: left;
    height: auto;
    width: 100%;
    max-width: 100%;
    min-width: 100%;
    padding: 14px;
    box-sizing: border-box;
    transition: border-color .15s cubic-bezier(.645, .045, .355, 1);
}
.label span {
    font-size: 11px;
