.no-content {
    text-align: center;
    font-weight: 600;
    margin-top: 35px;
    margin-bottom: 35px;
    font-size: 19px;
    color: hsla(0, 0%, 100%, 0.3);
}
.range {
    background: hsla(0, 0%, 100%, 0.2);
    white-space: nowrap;
    border: none;
    text-align: center;
    border-radius: 3px;
    font-size: 13px;
    font-weight: 600;
    color: #fff;
    width: 60px;
    z-index: 10;
    position: relative;
    padding: 6px 0 5px 0;
}
.button.green:hover {
    background: hsla(0, 0%, 100%, 0.3);
    color: hsla(0, 0%, 100%, 0.1);
    border: none;
}
