css:
@-moz-document domain("edu.p.lodz.pl") {
html, body {
    background-color: #333333;
    color: #d7d7d7;
}

body {
    background-color: #333;
}
.dropdown-menu {
    border: 3px solid silver;
    background-color: #424242;
}
.breadcrumb {
    background: none!important;
    background-color: #424242!important;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    -border-radius: 0px;
}
.nav .dropdown-menu li a {
    color: #008f97!important;
}
#page-header-wrapper {
    background-color: #333;
}
#sitetitle, #sitetitle a, #sitetitle p, #sitetitle h1 {
    color: #ffffff!important;
}
#above-header {
    border-bottom: 1px solid #fff0;
}
#above-header .headermenu .dropdown-menu a, #above-header .headermenu .dropdown-toggle a {
    color: #008f97!important;
}
.content-only #region-main-box, .content-only #region-main {
    background-color: #252525;
}
h1, h2, h3, h4, h5, h6 {
    color: #fff;
}

dt, dd {
    line-height: 20px;
    color: white;
}
.addt-title {
    background: #505050;
}

p {
    color: #c6c6c6;
}

a, a:visited, .tabtree .tabrow0 li a, .dropdown-menu.usermen>li>a {
    color: #09a9ae;
}

#region-main {
    background-color: #252525;
}

#page-header .socialbox {
    z-index: -10;
}

#site-news-forum h2 {
    color: white;
}

.breadcrumb li {
    color: white;
}

.course-content ul.topics li.section .content, .course-content ul.weeks li.section .content {
    background-color: #252525;
    
}

.sectionname {
    background-color: #5a5a5a!important;
    color: #ffffff!important;
}

#page .course-content ul li.section.main {
    background-color: #534e4e00;
    border: 0px solid #101010;
}

.section li.modtype_assign div.activity-wrapper {
    background-color: #3e3e3e;
}

li {
    color: #d0d0d0;
}

.section li.modtype_quiz div.activity-wrapper {
    background-color: #3e3e3e;
}

#region-main .mform:not(.unresponsive) .fitem .fitemtitle label {
    color: #d7d7d7;
}

.mform .fitem .fitemtitle div {
    color: #cacaca;
}

.mform fieldset.collapsible legend a.fheader {
    color: #f9f9f9;
}

table#form td.submit, .form-buttons, .path-admin .buttons, #fitem_id_submitbutton, .fp-content-center form + div, div.backup-section + form, #fgroup_id_buttonar {
    background-color: #3e3e3e;
    border-top: 1px solid #3e3e3e;
}

.btn, .btn a {
    border: 0px solid #ce1818;
}

.section li.modtype_forum div.activity-wrapper {
    background-color: #3e3e3e;
}

.block .header, #dockeditempanel .dockeditempanel_hd {
    background: #5a5a5a;
}

.block .header .title h2 {
    color: white;
}

div.no-overflow, .yui3-datatable {
    color: #d6d6d6;
}

.block>.content, #dockeditempanel .dockeditempanel_bd {
    background-color: #252525;
    color: #d1d1d1;
}

.block .content a, .block .content li a, .block .content td a {
    color: #09a9ae;
}

.breadcrumb ul i, .breadcrumb li.lastli span {
    color: #b9b9b9;
}

.jsenabled .block_settings .block_tree .tree_item.branch, .block_navigation .block_tree .tree_item.branch {
    color: #09a9ae!important;
}

#buttonsectioncontainer .divisortext {
    color: #d4d4d4;
}

.path-mod-forum .forumheaderlist .discussion .topic, .path-mod-forum .forumheaderlist .discussion .discussionsubscription, .path-mod-forum .forumheaderlist .discussion .topic.starter, .path-mod-forum .forumheaderlist .discussion .replies, .path-mod-forum .forumheaderlist .discussion .lastpost {
    background-color: #1d1d1d;
}

.path-mod-forum .forumheaderlist thead .header, .path-mod-forum .forumheaderlist tbody .discussion td {
    background-color: #1d1d1d;
}

.forumpost {
    background-color: #5b5b5b;
}

.path-mod-attendance table.attlist td.c0 {
    text-align: right;
    color: gainsboro;
}

table, table * td {
    color: #afafaf;
}

table#explaincaps tbody>tr:nth-child(odd)>td, table#defineroletable tbody>tr:nth-child(odd)>td, table.grading-report tbody>tr:nth-child(odd)>td, table#listdirectories tbody>tr:nth-child(odd)>td, table.rolecaps tbody>tr:nth-child(odd)>td, table.userenrolment tbody>tr:nth-child(odd)>td, table#form tbody>tr:nth-child(odd)>td, form#movecourses table tbody>tr:nth-child(odd)>td, #page-admin-course-index .editcourse tbody>tr:nth-child(odd)>td, .forumheaderlist tbody>tr:nth-child(odd)>td, table.flexible tbody>tr:nth-child(odd)>td, .generaltable tbody>tr:nth-child(odd)>td, table#explaincaps tbody>tr:nth-child(odd)>th, table#defineroletable tbody>tr:nth-child(odd)>th, table.grading-report tbody>tr:nth-child(odd)>th, table#listdirectories tbody>tr:nth-child(odd)>th, table.rolecaps tbody>tr:nth-child(odd)>th, table.userenrolment tbody>tr:nth-child(odd)>th, table#form tbody>tr:nth-child(odd)>th, form#movecourses table tbody>tr:nth-child(odd)>th, #page-admin-course-index .editcourse tbody>tr:nth-child(odd)>th, .forumheaderlist tbody>tr:nth-child(odd)>th, table.flexible tbody>tr:nth-child(odd)>th, .generaltable tbody>tr:nth-child(odd)>th {
    background-color: #272727;
}

#page {
    background-color: #333;
}

.preferences-content .preferences-groups, .preferences-header-bar {
    background-color: #424242;
}

.preferences-group>h3 {
     border-top-right-radius: 0px; 
     border-top-left-radius: 0px; 
}

body#page-user-preferences #region-main, body#page-user-preferences {
    background-color: #424242!important;
    background-color: #7a7a7a;
}

.preferences-group>h3 {
    background: linear-gradient(to right bottom,rgb(104, 104, 104) 0%,#404040);
}

.preferences-header-bar {
    background: linear-gradient(to bottom,#252525 0%,#252525);
    box-shadow: 0 12px 45px -8px rgba(43, 140, 139, 0);
}

logintile .logindesc {
    color: white;
}

.logintile {
    background: #6c6c6c;
    border: 0px solid #909090;
}

#region-main span[style] {
    color: white !important;
} 
}
