css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tvtorrents.com") {

*{
   font-family:verdana!important;}

#chatInput.tinput {
   width:495px!important;}

.container {
   margin-left:110px!important;}

.sidebox {
   background:none!important;
   background-color:#dbdeff!important;
   margin:1px!important;}

.subbox {
   background:none!important;
   background-color:#7799AA!important;}

.subbox>img {
   display:none!important;}

.boxhead {
   background:none!important;
   background-color:transparent!important;}

.container>a>img[align*="middle"] {
   margin-left:170px !important;}

.middle {
   margin-left:120px!important}

.boxhead h2 {
   background:none!important;
   background-color:#1222ab!important;
   padding: 4px!important;
   font-size: 15px!important;
   font-family:verdana!important;
   color: #eceeff!important;}

.boxbody {
   background:none!important;
   font-size:11px!important;
   font-family:verdana!important;
   padding:5px!important;
   margin-bottom:2px!important;}

.boxbody>div[style*="height: 90px"] {
   height: 100% !important;}

#boxhead_chatbox.boxhead>h2>a>img[border*="0"] {
   top:-45px !important;
   left:0px !important;}

#boxhead_chatbox.boxhead>h2>font[id*="chat_header_message"] font[size*="3"] {
   font-size: 9px !important;
   color: #eceeff!important;}

#boxhead_chatbox.boxhead>h2>font[id*="chat_header_message"] a {
   font-size: 9px !important;}

#boxhead_chatbox.boxhead>h2>font[id*="chat_header_message"] {
   font-size: 9px !important;
   color: #eceeff!important;}

.right {
   display:none!important;}

}
