css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("voirfilms.online") {

/* ==== 0- Stream - Voirfilms.Online Widescreen v.1 - WATnew06 ===== */

/* SUPP */
.title.single-titless.entry-title ,
footer#site-footer,
.tags ,
.header-social ,
.headres ,
.title.single-titlesss.entry-title + center ,
#player-wrapper,
.title.single-titless.entry-title +  center ,
.title.single-titlesss.entry-title ,
.single_post>header>.ulxfi+ center {
    display: none !important;
}
/* .title.single-titless.entry-title {
    color: #ffffff;
    font-size: 15px !important;
    height: 15px !important;
    line-height: 15px !important;
    margin-top: -25px !important;
} */
/* color */
/* ::selection , */
::-moz-selection {
 background: red;
 color:#ffffff;
}


/* WIDESCREEN */
#page.home-h2 {
    max-width: 100% !important;
    min-width: 100% !important;
    margin-top: 30px !important;
}

.main-container {
    max-width: 100% !important;
    min-width: 100% !important;
}


.postsby {
    margin-bottom: 7px !important;
}
.home-h2 .article {
    width: 85% !important;
}

.latestPost.excerpt.first ,
.latestPost.excerpt.last ,
.latestPost.excerpt {
    position: relative;
display: inline-block !important;
    float: none !important;
clear: none !important;
    width: 15% !important;
    margin-bottom: 0px !important;
    margin-left: 40px !important;
    margin-right: 30px !important;
    margin-top: 0% !important;
text-align: center !important;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
border: 1px solid red !important;
}
.featured-thumbnail {
display: inline-block !important;
text-align: center !important;
margin: auto !important;
}
.featured-thumbnail img {
    float: none !important;
/*     height: auto !important; */
max-height: 171px !important;
    margin-bottom: 0;
/* width: 100% !important; */
width: auto !important;
    max-width: 243px !important;
}

.latestPost header h2 {
    text-align: center !important;
}
.archive.category .navigation.pagination{
    position: absolute;
    width: 41% !important;
    top: -10px !important;
    right: 15% !important;
    padding: 5px !important;
    overflow: hidden;
    text-align: right !important;
    background: #00000073 none repeat scroll 0 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
}
.archive.category .navigation.pagination .nav-links{
    display: inline-block !important;
    text-align: right !important;
}

.home-h2 .sidebar.c-4-12 {
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    margin-bottom: 20px;
    margin-top: 1%;
    max-width: 370px;
    width: 15% !important;
}
div#page.single {
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    box-sizing: border-box;
    color: #939daf;
    float: right !important;
    margin-bottom: 30px;
    margin-top: 0px !important;
    padding: 5px !important;
    min-width: 70% !important;
/* background: aqua !important; */
}
.single-title {
    color: #ffffff;
    font-size: 15px !important;
    height: 15px !important;
    line-height: 15px !important;
    margin-top: -25px !important;
}

#site-header {
    position: relative;
    float: left;
    width: 100%;
/*     height: 0 !important; */
    height: 35px !important;
    margin-bottom: -20px !important;
    z-index: 99;
    background: #27415f none repeat scroll 0 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
}

#site-header .container {
    max-width: 100% !important;
    min-width: 100% !important;
    background: green !important;
}
#header {
    float: left;
    margin: 0;
    padding: 0;
    position: relative;
    text-align: center;
    width: 100%;
/*     height: 0 !important; */
    height: 35px !important;
    background-image: none !important;
}

.logo-wrap {
    box-sizing: border-box;
    display: inline-block;
    float: left;
    overflow: hidden;
    padding: 0 0 0 25px;
    height: 30px !important;
}
#header h1, 
#header h2 {
    display: inline-block;
    float: left;
    line-height: 1;
    position: relative;
    height: 30px !important;
    margin-bottom: 0 !important;
    margin-top: 0 !important;
}
#logo a {
    float: left;
    height: 30px !important;
    line-height: 1;
    color: #33bcf2;
}
#logo img {
    float: left;
    height: 30px !important;
    width: 44px !important;
}
#header .header-search {
    display: inline-block;
    float: right !important;
    max-width: 453px;
    text-align: left;
    width: 100%;
    height: 30px !important;
    margin-top: 0 !important;
}
#header #searchform {
    float: left;
    width: 100%;
    height: 30px !important;
}
#searchform fieldset {
    float: left;
    width: 100%;
    height: 30px !important;
}
#header #s {
    height: 30px !important;
    background-attachment: fixed;
    background-color: #4b576f;
    background-image: none !important;
    background-position: left top;
    background-repeat: no-repeat;
    background-size: cover;
}
.sbutton, #searchsubmit {
    height: 30px !important;
    background: transparent none repeat scroll 0 0;
    border: 0 none;
    color: #a4a4a4;
    cursor: pointer;
    float: right;
    padding: 0px !important;
    position: absolute;
    right: 0;
    text-align: center;
    top: 0;
    transition: all 0.25s linear 0s;
    vertical-align: top;
    width: 42px;
    z-index: 10;
}
#searchform .fa-search {
    height: 20px !important;
    color: #fff;
    font-size: 20px;
    height: 24px;
    padding: 5px !important;
    position: absolute;
    right: 0;
    text-align: center;
    top: 0;
    width: 24px;
}
#primary-navigation {
    display: block;
    float: left !important;
    text-align: left;
    width: 100%;
    min-width: 100% !important;
    max-width: 100% !important;
    height: 30px !important;
    margin-top: -52px !important;
margin-left: 270px !important;
/*     background: red !important; */
}
.navigation.clearfix.mobile-menu-wrapper {
/*     float: left; */
    height: 30px !important;
}
#primary-navigation.sticky-navigation-active {
    backface-visibility: hidden;
    position: fixed;
    top: 0px !important;
    margin-top: 0px !important;
    z-index: 500;
}

#primary-navigation #menu-menu {
    height: 30px !important;
}
#primary-navigation .menu-item {
    height: 30px !important;
}
#primary-navigation a {
    height: 30px !important;
    box-sizing: border-box;
    padding: 2px 30px !important;
}
   
.wrapper-plugin-video{
    position: fixed;
    top: 55px !important;
left: 10px !important;
    width: 29%;
background: #222 !important;
}

.photosd {
    float: left;
    height: auto !important;
    margin-right: 10px;
    opacity: 1;
    width: 21%;
}

#commentsAdd {
    clear: both;
    float: left;
    margin-bottom: 0;
    margin-top: 20px;
    width: 29%;
    position: fixed;
    left: 4px;
    bottom: 0;
    height: 34px;
transition: all ease 0.7s !important;
}
#commentsAdd:hover {
    clear: both;
    float: left;
    margin-bottom: 0;
    margin-top: 20px;
    width: 29%;
    position: fixed;
    left: 4px;
    bottom: 0;
    height: 582px !important;
}





/* ==== END  ======= */

}
