css:
@-moz-document url-prefix("https://www.gamestar.de/") {
/* ==UserStyle==
@name         Gamestar Darkmode
@version      1.0.0
@description  Darkmode für die Gamestar Seite.
@author       Makney
==/UserStyle== */

.taglist {
 background-color: rgb(21, 29, 32);
}
.tag {
 color: #fff;
}
#header-search, #header-trending, .trenner {
 border-bottom: 1px solid rgb(38, 49, 55);
}
#header-search, #header-trending {
 transition: height .1s ease 0s;
 background-color: rgb(21, 29, 32);
}
#content .container {
 background: rgb(21, 29, 32);
}
body {
	background-color: rgb(21, 29, 32);
	color: rgb(240,240,240)!important;
}
.info-box, .link-box {
	background: rgb(38, 49, 55);
}
.box .box-title a, .box .box-title span {
	background: rgb(21, 29, 32);
}
.box .box-link-top a {
	background: rgb(21, 29, 32);
}
.breadcrumb {
	background-color: rgb(21, 29, 32);
}
.imagecontainer .onimage .sub {
	background-color: rgb(38, 49, 55);
	color: rgb(240,240,240);
}
.div-product-title .label {
	color: rgb(240,240,240);
	background-color: rgb(38, 49, 55);
}
.btn-tab.active {
	background: #0055b9;
	color: rgb(240,240,240) !important;
	border-bottom: 2px solid #004494;
}
.btn-tab:hover:not(.active) {
	background: #0055b9;
	border-bottom: 2px solid #004494;
}
.btn-link-gray {
	color: rgb(240,240,240) !important;
}
.page-header #search_wrap #search_text {
	background-color: rgb(21, 29, 32);
	color: rgb(240,240,240);
}
.page-header #search_wrap #search_text.open {
	border: 1px solid rgb(38, 49, 55);
	border-bottom: 2px solid rgb(38, 49, 55);
}
.p.caption, p.caption {
 color: rgb(240,240,240);
}
.h3 .ellipsize3::after, .h3.ellipsize3::after, h3 .ellipsize3::after, h3.ellipsize3::after {
	background: rgb(21, 29, 32);
}
.h4 .ellipsize2::after, .h4.ellipsize2::after, h4 .ellipsize2::after, h4.ellipsize2::after {
 background: rgb(21, 29, 32);
}
.p .ellipsize3::after, .p.ellipsize3::after, p .ellipsize3::after, p.ellipsize3::after {
	background: rgb(21, 29, 32);
}
.p .ellipsize2::after, .p.ellipsize2::after, p .ellipsize2::after, p.ellipsize2::after {
	background: rgb(21, 29, 32);
}
.ellipsize2::after, .h4 .ellipsize3::after, .h4.ellipsize3::after, h4 .ellipsize3::after, h4.ellipsize3::after {
 background: rgb(21, 29, 32);
}
[class*=" icon-"], [class^="icon-"] {
	color: rgb(240,240,240);
}
}

@-moz-document url("https://www.gamestar.de/") {
.home h2 {
 background: #0055b9;
 color: #fff;
}
.home [class*=" content-item-"], .home [class^="content-item-"] {
 background-color: rgb(38, 49, 55);
}
.home .category, .content-item-medium .category {
 background: #0055b9;
 color: #fff !important;
 padding: 6px 10px;
}
.home .plus .category {
	color: #fff !important;
	background: #26A65B;
}
.home .content-item-small > h3 {
 font-size: .825rem;
 font-weight: normal;
 margin-bottom: 0;
}
.home .grid {
 display: -ms-grid;
 display: grid;
 grid-gap: 16px 32px;
}
.home [class*=" content-item-"], .home [class^="content-item-"] {
 padding-bottom: 8px;
 margin-bottom: 8px;
 border-bottom: 1px solid #fff;
}
.home [class^="content-item-"] {
 background-color:  rgb(38, 49, 55);
}
.home [class*=" content-item-"] .intro, .home [class^="content-item-"] .intro {
 margin: 0 0 8px;
 margin-right: 0px;
 font-size: .875rem;
 line-height: 1.1;
}
.home .intro {
 color: rgb(240,240,240);
}
.home .info-small {
 line-height: 2;
 color: rgb(240,240,240);
 margin-right: 8px;
}
.home .comments::before {
 color: rgb(240,240,240);
}
.home .comments {
 color: rgb(240,240,240)
}
#section2.grid>div {
 background: rgb(38, 49, 55);
}
#section2 [class*="content-item-"], #section2 [class^=content-item-] {
 background-color: rgb(38, 49, 55);
 padding: 8px;
}
.home h3 {
 margin-left: 5px;
}
.home .poll-answer-row {
 color: #fff;
}
 #content > .home .container.full-width {
 border-top: 1px solid #fff;
 border-bottom: 1px solid #fff;
 background-color: rgb(38, 49, 55);
}


}

@-moz-document url("https://www.gamestar.de/spiele/") {
.product-search input {
 border: 1px solid rgb(38, 49, 55);
 background-color: rgb(38, 49, 55);
}
.product-filter .select-wrapper select, .product-sorter .select-wrapper select {
 background-color: rgb(38, 49, 55);
 color: rgb(240,240,240);
}
.p.caption, p.caption {
 color: rgb(240,240,240);
}
.p.info, p.info {
 color: rgb(240,240,240);
}
}

@-moz-document url-prefix("https://www.gamestar.de/artikel/") {
.article .blockquote, .article blockquote, .comment .blockquote, .comment blockquote, .kasten {
 color: rgb(240,240,240);
 border: 1px solid rgb(240,240,240);
 background: rgb(38, 49, 55);
}
.article .blockquote .p, .article .blockquote p, .article blockquote .p, .article blockquote p {
 color: rgb(240,240,240);
}
.comment textarea {
 background-color: rgb(38, 49, 55);
 border: 1px solid rgb(240,240,240);
}
.textarea, textarea {
 color: rgb(240,240,240);
}
.comment .answers .answer {
 background-color: rgb(38, 49, 55);
}
.comment .answers .answer-triangle {
 border-color: transparent transparent transparent rgb(21, 29, 32);
}
}

@-moz-document url-prefix("https://www.gamestar.de/plus/") {
.plus-box {
 background-color: rgb(38, 49, 55);
 color: rgb(240,240,240);
}
.plus.info-box {
 background-color: rgb(38, 49, 55);
}
.input, input:not([type="range"]) {
 border: 1px solid rgb(38, 49, 55);
 background: rgb(38, 49, 55);
 color: rgb(240,240,240);
}
#content .article .blockquote .tag, #content .article blockquote .tag, #content .comment .blockquote .tag, #content .comment blockquote .tag, #content .kasten .tag, .article #content .blockquote .tag, .article #content blockquote .tag, .comment #content .blockquote .tag, .comment #content blockquote .tag {
 background: rgb(38, 49, 55)!important;
}
}
