css:
@-moz-document domain("alza.cz") {
/* Alza Fix by R3gi, Czech Republic
Version: 2019.05.09.21:25; */

#body2.layout-1 #page,
#body2.layout-1 #header,
#body2.layout-1 #footer {
    width: 100%;
}

.layout-1 .categoryPage.withLeftParametrization .mainContent,
#body2.layout-1.cpg #content2 {
    width: 89vw;
}

#content0c,
.layout-1 #footerc #alzaBenefits {
    width: auto !important;
}

#detailPicture {
    width: 30%;
}

.layout-1 #detailItem #detailText {
    width: 60%;
}

.layout-1 #detailItem .variantListView.showcolapser .container .collapsed {
    left: -5%;
    width: 105%;
}

.leftBanner,
.rightBanner {
    display: none;
}

#page {
    margin: 0;
}

.categoryPage.withLeftParametrization {
    display: flex;
}
}
