css:
@-moz-document domain("to-do.live.com") {
/* Top bar */
.o365sx-navbar,
.o365cs-base .o365sx-waffle,
.o365cs-base .o365sx-button,
.o365cs-base .o365sx-button:focus,
.o365cs-base .o365sx-appName {
    background-color: #ae3030 !important;
}

.o365cs-base .o365sx-waffle:hover,
.o365cs-base .o365sx-button:hover {
    background-color: #d53b3b;
}

.o365cs-base .o365sx-button:active,
.o365cs-base .o365sx-activeButton,
.o365cs-base .o365sx-activeButton:hover,
.o365cs-base .o365sx-activeButton:focus {
    color: #252423 !important;
    background-color: #faf9f8 !important;
}


/* Search field */
.searchToolbar-icon.search:last-child:after,
.sidebar-header .fontIcon,
.o365header .searchToolbar .fontIcon {
    color: #ae3030;
}

/* Buttons */
.button.blue,
.pill-35,
.pill-41,
.pill-81,
.pill-68,
.pill-45 {
    background-color: #c62424;
}
.button.blue:hover,
.pill-35:hover,
.pill-41:hover,
.pill-81:hover,
.pill-68:hover,
.pill-45:hover {
    background-color: #e12e2e;
}

/* Middle list */
.taskItem-title {
    font-size: 1.5rem;
    font-family: "Lato", "Geneva CY", "Lucida Grande", "Arial Unicode MS", "Helvetica Neue", "Helvetica", "Arial", sans-serif
}

.chunkedComponentList .chunkedScrollContainer,
#main,
.addTask,
#main .main-background {
    background-color: #f4f2ef;
}

.backgroundLines {
    background: -webkit-linear-gradient(270deg, #f4f2ef, #f4f2ef 52px, #e5e5e5 52px, #e5e5e5 52px);
    background: -moz-linear-gradient(270deg, #f4f2ef, #f4f2ef 52px, #e5e5e5 52px, #e5e5e5 52px);
    background: -o-linear-gradient(270deg, #f4f2ef, #f4f2ef 52px, #e5e5e5 52px, #e5e5e5 52px);
    background: -ms-linear-gradient(270deg, #f4f2ef, #f4f2ef 52px, #e5e5e5 52px, #e5e5e5 52px);
    background: linear-gradient(180deg, #f4f2ef, #f4f2ef 52px, #e5e5e5 52px, #e5e5e5 52px);

    -webkit-background-size: 100% 53px;
    -moz-background-size: 100% 53px;
    background-size: 100% 53px;
}

.taskItem:hover .taskItem-body {
    background-color: #f7eee3;
    -webkit-box-shadow: inset 0 0 0 1px #d2c7b5;
    box-shadow: inset 0 0 0 1px #d2c7b5;
}

.taskItem.selected .taskItem-body {
    background: #eee1d0;
    -webkit-box-shadow: inset 0 0 0 1px #d2c7b5;
    box-shadow: inset 0 0 0 1px #d2c7b5;
}

/* Task header */
.detailHeader .editableContent .editableContent-display {
    margin-bottom: 1px;
    /* Fixes small offset glitch when switching to textbox on clicking. */
}

/* My Day activated */
.details .section-item.isSet .section-title, .details .section-item.isSet .section-icon .fontIcon {
    color: #ae3030;
}

.taskItemInfo-myDay .taskItemInfo-label, .taskItemInfo-myDay .icon svg {
    color: #ae3030;
    fill: #ae3030;
}

/* Task details */
.detailNote .editableContent .editableContent-display,
.detailNote .editableContent-edit .editableContent-textarea {
    /*font-size: 1.4rem;*/
    /*font-weight: 400;*/
    /*line-height: 1.3rem;*/
}

.details .section-item.detailNote.edit {
    border-color: #c84444;
}

/* This overrides default windows background color. */
.editableContent-textarea {
    background-color: #fff;
}

/* Remove blue focus borders */
.editableContent-textarea:focus,
.ms-Fabric--isFocusVisible .editableContent-textarea:focus {
    box-shadow: none !important;
}
}
