@-moz-document url-prefix("https://aternos.org/start/") {
/* Main */
::-webkit-scrollbar {
	display: none !important;
}

body {
	background-image: url(https://www.pixelstalk.net/wp-content/uploads/2016/06/Black-and-Red-Photos-HQ.jpg) !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}

@keyframes logs
    {
      0%   {background: none;}
      50%   {background: red;}
      100%   {background: none;}
    }

.alert-container main {
	background: rgba(0, 0, 0, 0.6) !important;
}

.alert-container main {
	color: white !important;
}

.alert-container main input {
	color: #B52637 !important;
}

.alert-container header {
	color: black !important;
}

.alert-container header .fas {
	font-weight: 10 !important;
}

/* Header */
.header {
	background: rgba(0, 0, 0, 0.6) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/MUT8R3j.png);
}

.social a {
	color: #B52637 !important;
}

.social a:hover {
	color: #93032E !important;
}

.statusinfo {
	color: #B52637 !important;
}

.username, .usericon {
	color: #B52637 !important;
    font-weight: bold !important;
}

.username:hover, .usericon:hover {
	color: #93032E !important;
}

.friend-access-dropdown {
	background: rgba(0, 0, 0, 0.8) !important;
    border: none !important;
}

.friend-access-switch {
	border-bottom: 1px solid rgba(255, 255, 255, 0.5) !important;
    padding-top: 5px !important;
    padding-top: 5px !important;
}

.friend-access-switch-ip {
	color: #B52637 !important;
}

.friend-access-switch-user {
	color: white !important;
    font-weight: bold !important;
}

.navigation a .navigation-log-problems-badge {
	animation: logs 2.5s infinite !important;
}

/* Sidebar */
.sidebar {
	background: rgba(0, 0, 0, 0.4) !important;
}

/* Navigation */
.navigation {
	background: rgba(0, 0, 0, 0.6) !important;
}

.navigation a {
	border: none !important;
    text-shadow: 0 0 2px black !important;
}

.navigation a:hover {
	background: rgba(0, 0, 0, 0.6) !important;
}

.navigation a:nth-child(odd) {
	color: #B52637 !important;
}

.navigation a:nth-child(even) {
	color: White !important;
}

.navigation .fas {
	font-weight: 10 !important;
    text-shadow: 0 0 0 !important;
}

/* Content */
.content {
	background: rgba(0, 0, 0, 0.4) !important;
}

.content h1 {
	color: #B52637 !important;
}

.content h2 {
	color: white !important;
    text-shadow: 0 0 5px black !important;
}

.tutorials {
	text-align: center !important;
    margin: auto !important;
}

.tutorials a {
	border: 1px solid #B52637 !important;
    background: rgba(0, 0, 0, 0.6) !important;
    box-shadow: 0 0 15px black !important;
    -webkit-border-radius: 10px !important;
}

.tutorials a:hover {
	background: rgba(0, 0, 0, 0.8) !important;
}

.start-activity {
	background: rgba(43, 135, 211, 0.6) !important;
}

.start-activity:hover {
	background: rgba(27, 111, 181, 0.6) !important;
}

.start-activity-help .start-activity {
	background: rgba(31, 215, 141, 0.6) !important;
}

.start-activity-help .start-activity:hover {
	background: rgba(25, 168, 108, 0.6) !important;
}

.start-activity .fas, .start-activity .far, .start-activity .fab {
	font-weight: 10 !important;
}

/* Colors:
Main red: #B52637
Darker red: #93032E
Dark: #1C191A
*/
