css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("japtem.com")
{
/*!========================================\\
|| Dark Japtem                             ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for Japtem          ||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/

  ::-moz-selection {
    background: #444444;
    color: #bbbbff;
  }

  h1, h2, h3, h4, h5, h6, div#content, input, button, pre, p, div {
    color: #DDDDDD !important;
  }

  a {
    color: #aaaaff !important;
  }

  a:visited {
    color: #ffaaff !important;
  }

  a.external {
    color: #aaffaa !important;
  }

  a.new {
    color: red !important;
  }

  #content, #main {
    background-color: #222222 !important;
  }
  
  aside, #wrapper, .fusion-page-title-bar, .fusion-header {
    background-color: #000000 !important;
  }

  .fusion-page-title-bar {
    background-image: none;
  }

  #disqus_thread, footer {
    display:none;
  }

  .post-content p {
    margin: 0;
  }

  a, h2 {
    font-family: inherit !important;
  }

  #custom_html-2 { /* discord widget */
    display: none;
  }

  #wrapper {
    max-width: 1560px;
  }

  #main .fusion-row {
	margin: 0;
    max-width: 1560px;
  }

  #sidebar {
    width: 300px !important;
    padding: 10px !important;
  }

  #content {
    width: calc(100% - 300px - 20px) !important;
  }

  #content .post-content h2:last-child {
    margin-top: 15px;
  }

}
