css:
@-moz-document url-prefix("https://www.digicamclub.de/forum.php"), url-prefix("https://www.digicamclub.de/forumdisplay.php"), url-prefix("https://www.digicamclub.de/showthread.php") {
html {
    background: #202020;
}
body {
    background-color: #202020;
    color: #202020;
}
body a {
    color:#303030;
}
body a:hover, body a:hover .time, body a:hover .shade, body a:hover .understate {
    color:#417394;
}
.navtabs {
    background:#696969;
}
.navtabs ul {
    border-top: 1px solid #505050;
}
.navtabs li {
    border-right: 1px solid #505050;
}
.navtabs li:first-child {
    border-left: 1px solid #505050;
}
.navtabs li a.navtab {
    background:#696969;
}
.navbar_search input {
    border: 1px solid #505050;
}
.globalsearch .textboxcontainer {
    border-left: 1px solid #505050;
}
.globalsearch .buttoncontainer {
    border-left: 1px solid #505050;
    border-right: 1px solid #505050;
}
.navlinks {
    background:#a7a7a7;
}
.body_wrapper {
    background:#505050;
}
.wgo_block {
    box-shadow: -2px 2px 2px #404040;
}
.wgo_block .blockbody {
    background:#a7a7a7;
}
.forumhead {
    background:#696969;
    border: 1px solid #404040;
}
.forumrow.table {
    background: #a7a7a7;
}
.forum_info .blockhead {
    box-shadow: -2px 2px 2px #404040;
}
.forum_info .blockbody {
    background:#a7a7a7;
    box-shadow: -2px 2px 2px #404040;
}
.forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow {
    background:#a7a7a7;
}
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
    box-shadow: -2px 2px 2px #404040;
}
.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    background:#a7a7a7;
}
#pagetitle h1 {
    color:#202020;
}
#pagetitle .description {
    color:#202020;
}
.blockhead {
    background:#696969;
    border: 1px solid #404040;
}
.footer {
    background:#696969;
    border: 1px solid #404040;
}
.textbox, textarea, select {
    border: 1px solid #505050;
}
#sidebar_container a {
    color: #202020;
}
#sidebar_container > ul {
    background: #696969;
    border: 1px solid #404040;
    box-shadow: -2px 2px 2px #404040;
}
#sidebar_container .blocksubhead {
    color: #202020;
}
#sidebar_container .blockbody {
    background: #404040;
    border: 1px solid #404040;
}
#sidebar_container .blockrow {
    background:#a7a7a7;
}
#sidebar_container #gallery_block a {
    color: #3e3e3e;
}
.announcements .announcerow {
    background:#a7a7a7;
    box-shadow: -2px 2px 2px #404040;
}
.announcerow .announceinfo, .announcerow .announcement {
    background:#a7a7a7;
}
.threadlisthead {
    background:#696969;
    border: 1px solid #404040;
    box-shadow: -2px 2px 2px #404040;
}
.threadbit {
    box-shadow: -2px 2px 2px #404040;
}
.threadbit .alt {
    background:#a7a7a7;
}
.threadbit .sticky {
    background:#858585;
    border: 1px solid #404040;
}
.threadbit .nonsticky, .threadbit .discussionrow {
    background:#a7a7a7;
    border: 1px solid #404040;
}
.thread_info h4 {
    box-shadow: -2px 2px 2px #404040;
}
.thread_info .blockbody {
    box-shadow: -2px 2px 2px #404040;
    background:#a7a7a7;
}
.newcontent_textcontrol {
    background:#202020;
}
.toolsmenu {
    background: #a7a7a7;
    border: 1px solid #404040;
}
.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    background:#a7a7a7;
}
.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background:#696969;
    border: 1px solid #404040;
}
.postbitlegacy .postfoot .textcontrols a.report, .postbit .postfoot .textcontrols a.report {
    background: url(https://www.digicamclub.de/images/buttons/report-40b.png) no-repeat #a7a7a7 left;
}
.postbitlegacy .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.newreply {
    background: url(https://www.digicamclub.de/images/buttons/quote_40b.png) no-repeat #a7a7a7 left;
}
.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
    background: url(https://www.digicamclub.de/images/buttons/multiquote-back_40b.png) no-repeat #a7a7a7 left;
}
.postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.editpost, .eventbit .eventfoot .textcontrols a.editevent {
    background: url(https://www.digicamclub.de/images/buttons/buttons/edit_40b.png) no-repeat #a7a7a7 left;
}
.postbit .posttitle, .postbitlegacy .title {
    border-bottom: 1px solid #404040;
}
.postbit, .postbitlegacy, .eventbit {
    border: 1px solid #404040;
}
.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    border-left: 1px solid #404040;
    background: #e3e3e3;
}
.postbitlegacy .userinfo {
    background: #a7a7a7;
}
.postbitlegacy .postdetails, .eventbit .eventdetails {
    background: #a7a7a7;
}
.bbcode_container div.bbcode_quote {
    background: #f3f3f3;
    border: 1px solid #404040;
}
.pagination span a {
    border: 1px solid #404040;
    color: #404040;
    background: #d3d3d3;
}
.pagination span a.popupctrl {
    background: #d3d3d3;
}
.pagination span.selected a {
    background: #202020;
    border: 1px solid #404040;
}
.footer {
    box-shadow: -2px 2px 2px #404040;
}
}

