@-moz-document url-prefix("https://funpay.com/") {
    .wrapper {
        background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed;
        /* просто замени  ссылку */
    }
    .content-orders {
        background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed;
        /* просто замени  ссылку */
    }
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 14px;
        line-height: 1.428571429;
        color: #ff8949;
        background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed;
        /* просто замени  ссылку */
    }
    .profile-cover-img {
        background-clip: border-box;
        background: url(https://wallpaperaccess.com/full/755331.jpg) center;
        /* Полусфера в профиле. */
        background-size: 55% auto;
  }
    
.chat-promo {
    border: 1px solid #ff8949;
    background: #2d2d2d61;
}

    .cd svg {
        background: url(https://wallpaperaccess.com/full/755331.jpg) no-repeat center;
        /* все сферы везде. */
        background-size: 295% auto;
    }
    .profile-cover {
        overflow: unset;
    }
    .counter-list {
    margin-right: 195px;
}
.counter-list .counter-item {
    background: #ffa14952;
     color: #1ce0c9;
}
.nav-abc .nav>li>a {
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
    display: contents;
    padding: 3px;
    font-size: 16px;
    font-style: normal;
    -webkit-text-stroke: 0.65px #09073b;
    font-family: cursive;
}

    .nav-abc ul .active a, .nav-abc ul .active a:hover, .nav-abc ul .active a:focus {
    text-decoration: none;
    cursor: default;
    color: #ffaa00ed;
}
}


@-moz-document url-prefix("https://funpay.com/") {
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 14px;
        line-height: 1.428571429;
        color: #ff8949;
        /* Цвет текста основной */
        background-color: #000;
    }
    a {
        color: #54c7a3;
        /* Цвет текста надпись продавец\аккаунты\услуги\*** */
        text-decoration: none;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .panel {
        background-color: #423e3e61;
    }
    .contact-item:hover {
        background: rgba(236, 247, 18, .16);
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .panel-default > .panel-heading {
        background-color: #292929;
        border-color: #2d2d2d61;
    }
    .tc.table-hover .tc-item.warning:hover {
        background-color: #71632ead;
    }


    .chat-not-selected .chat-message-container {
        border-top: 1px solid #90f;
    }
    .chat-not-selected .chat-message-container {
        border-bottom: 1px solid #90f;
    }
    .chat-message-container {
        border-left: #90f 1px solid;
        border-right: #90f 1px solid;
    }

    .dropdown-menu > li > a:hover,
    .dropdown-menu > li > a:focus {
        color: #54c7a3;
        background-color: #71632ead;
    }
    .navbar-default .navbar-nav > .open > a,
    .navbar-default .navbar-nav > .open > a:hover,
    .navbar-default .navbar-nav > .open > a:focus {
        color: #54c7a3;
    }



    .btn-default:active,
    .btn-default.active,
    .open > .btn-default.dropdown-toggle {
        color: #4384d0;
        background-color: #ff6c1130;
        border-color: #4384d0;
    }


    .btn-default:active:hover,
    .btn-default:active:focus,
    .btn-default:active.focus,
    .btn-default.active:hover,
    .btn-default.active:focus,
    .btn-default.active.focus,
    .open > .btn-default.dropdown-toggle:hover,
    .open > .btn-default.dropdown-toggle:focus,
    .open > .btn-default.dropdown-toggle.focus {
        color: #4384d0;
        background-color: #ff6c1130;
        border-color: #4384d0;
    }

    .chat-message-list-date .inside {
        background: #915116;
        border-left: #ffd0004a 1px solid;
        border-right: #ffd0004a 1px solid;
        color: #2effd7db;
    }

    .chat-message-list-date:before {
        left: -20px;
        right: -20px;
        top: 50%;
        height: 0.1px;
        background: #ffd0004a;
    }

    .contact-item-message {
        color: #ffffff73;
    }

    .contact-item.active,
    .contact-item.active .contact-item-message {
        color: #ffffffd4;
    }

    .contact-item.active,
    .contact-item.active:hover,
    .contact-item.active:focus {
        background: #6f6dff75;
    }

    .contact-item.unread {
        background: #ff9d00a1;
    }
    .contact-item.unread,
    .contact-item.unread .contact-item-message {
        color: #ffffffd4;
    }


    .chat-form {
        border: #8924b100 1px solid;
    }

    .chat-form-input .form-control,
    .chat-form-input .hiddendiv {
        padding: 15px 10px 10px;
    }


    .badge {
        display: inline-block;
        min-width: 10px;
        padding: 3px 7px;
        font-size: 12px;
        font-weight: 500;
        color: #fff;
        line-height: 12px;
        vertical-align: middle;
        white-space: nowrap;
        text-align: center;
        background-color: #20ff005e;
        border-radius: 10px;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .panel-default > .panel-heading {
        color: #66b7ff;
        /* Цвет текста чат с продавцом */
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .form-control {
        border: 1px solid #292929;
        background-color: #423d3d;
        color: #54c7a3;
        /* Цвет текста надпись в выборе оплаты QIWI и блаблабла */
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .navbar-default {
        background-color: #41d2bc00;
    }

    .review-item-answer {
        display: inline-block;
        padding: 16px 20px;
        background: #00e5ff1a;
        border-radius: 4px;
        position: relative;
        color: #72fff1e0;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .navbar-default .navbar-nav > li > a {
        color: #54c7a3;
        /* Цвет текста надпись верхней строки (мои покупки итд) */
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .nav-tabs > li.active > a,
    .nav-tabs > li.active > a:hover,
    .nav-tabs > li.active > a:focus {
        color: #19d3ff;
        /* Цвет текста в кнопке выбранного  раздела */
        background-color: #000000;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    table.table-clickable tbody tr a {
        color: #14e6a4;
        /* Цвет ников продавцов */
        text-decoration: none;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .text-muted {
        color: #bce6cf;
        /* Цвет текста под никами. (на сайте 1337 лет итд) */
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .form-control[disabled],
    .form-control[readonly],
    fieldset[disabled] .form-control {
        background-color: #484343;
        /* бокс заплачу */
    }

    .payment-title {
        color: #8df1ece3;
    }
    .bootstrap-select .dropdown-menu > li > a {
        color: #34c3e3f0;
    }


    .chat-header {
        border: #bd59be 1px solid;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .chat-contacts,
    .chat-detail {
        background: #26272d;
        border: #90f 2px solid;
    }
    .contact-item {
        border-bottom: #000 1px;
    }
    .chat-full-header {
        border-bottom: #90f 1px solid;
    }
    .chat {
        border-bottom: 1px solid #90f;
    }
    .chat {
        border-top: 1px solid #90f;
    }
    .alert-info {
        background-color: #ff002921;
        border-color: #ffef00;
        color: #ffffffe0;
    }
    .custom-scroll::-webkit-scrollbar,
    .chat-message-list::-webkit-scrollbar,
    .chat-empty::-webkit-scrollbar,
    .chat-form-input .form-control::-webkit-scrollbar,
    .chat-form-input .hiddendiv::-webkit-scrollbar {
        background: #e600ff29;
        width: 5px;
        height: 1px;
    }
    .custom-scroll::-webkit-scrollbar-thumb,
    .chat-message-list::-webkit-scrollbar-thumb,
    .chat-empty::-webkit-scrollbar-thumb,
    .chat-form-input .form-control::-webkit-scrollbar-thumb,
    .chat-form-input .hiddendiv::-webkit-scrollbar-thumb {
        background: #ada1ff96;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .bg-light-color #header,
    .bg-light-color .wrapper-footer {
        background: transparent;
    }
    .chat-full .chat {
        background-color: #0000007a;
    }
    .media-user-name {
        color: #00ccffb5;
    }
    .chat {
        background-color: #0000008f;
    }
    .media-user-name a {
        color: #00ffb3c4;
    }

    .bg-light-color {
        background-color: #fff0
    }
    .game-title a {
        color: #ffaa00ed;
        text-decoration: none;
    }
    .wrapper-content {
        background: #00000057;
    }
    .navbar-right.logged .dropdown-menu {
        background-color: #282828db;
    }
    .user-link-name {
        color: #00ffb3c4;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .content-with-cd-wide {
        background: #0000007a;
        border: 1px solid #feff00;
        border: 1px solid rgba(236, 247, 18, .16);
        border-radius: 30px;
        outline: 0;
    }
    a.tc-item {
        color: #00ffb3c4;
        text-decoration: none;
    }
    .cd {
        position: relative;
        z-index: 100;
        border-radius: 50%;
        width: 400px;
        height: 400px;
    }
    a.cd-satellite {
        color: #00ffb300;
        z-index: 1;
        border-radius: 1%;
        width: 1px;
        height: 1px;
        background: #4384d000;
    }
    .offer {
        background: #0000003b;
    }
    .tc.table-hover .tc-item.warning {
        background-color: #9e1c1c29;
    }
    .tc {
        background-color: #00000080
    }


    .tc-finance {
        border-top: 1.3px solid #322f34;
        border-bottom: #322f34 1.3px solid;
        border-left: #322f34 1px solid;
        border-right: #322f34 1px solid;
        border-radius: 5px;
    }
}

@-moz-document url-prefix("https://funpay.com/") {
    .modal-content {
        background-color: #000c;
        border: 1px solid #999;
        border: 1px solid rgba(236, 247, 18, .16);
        border-radius: 60px;
        -webkit-box-shadow: 0 3px 9px rgba(0, 0, 0, 0.5);
        box-shadow: 0 3px 9px rgba(0, 0, 0, 0.5);
        background-clip: padding-box;
        outline: 0;
    }
    label.control-label {
        color: #ffba4cc4
    }
    .counter-item {
        background: #f3a0291c;
    }
    .counter-item.active {
        background: #f3a02994;
        color: #00d9ff;
    }
    .form-control-box {
        background: #84848400;
        border: 1px solid #84848400;
    }
    h5,
    .h5,
    .form-group > label {
        color: #14e6a4bf
    }
    .bootstrap-select .dropdown-toggle .filter-option {
        background-color: #595a46ab;
    }
    .bootstrap-select .dropdown-menu.inner {
        background-color: #595a46c2;
    }
}

