css:
@-moz-document domain("dramacool9.co"), domain("dramacool9.io"), domain("www13.watchasian.co"), domain("dramacool.movie")
{html,
body {
    background-color: #000000 !important;
    background: #000;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 12px;
    color: white;
}
.logo {
    height: 73px;
    width: 970px;
    background: url(https://i.imgur.com/XN7By6T.jpg) left top no-repeat !important;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    display: block;
}
.ads-evt {
    opacity: 0;
}
.container {
    background: #1c1c1c;
}
.block {
    background: #1c1c1c;
}
.anime_muti_link {
    color: #3ec2cf;
}
.anime_muti_link ul li:nth-child(2n+1) {
    background-color: #282828;
}
.list-episode-item-2 li:hover,
.list-episode-item-2 li:nth-child(even) {
    background: #282828;
}
.content-left {
    width: 100%;
}
.content-right {
    display: none;
}
.block,
.ads_place {
    border: none;
}
.plugins2 {
    margin-top: -2px;
}
.plugins2 .direction a {
    color: #ffffff;
    background: #282828;
}
.plugins2 .direction select,
.plugins2 .direction select option {
    background: #3EC2CF;
    color: #fff;
    -webkit-appearance: none;
    border-radius: 6px;
    margin-right: 0px;
    margin-left: 2px;
    padding: 6px;
}
.show-all {
    position: absolute;
    right: 22px;
    top: 5px;
    cursor: pointer;
    color: #3EC2CF;
}
.tab li:hover, .tab li.selected {
    background: #FDB813;
    margin-left: 10px;
}
.search {
    height: 34px;
    overflow: hidden;
    position: absolute;
    top: 50px;
    right: 0px;
    z-index: 10;
}
.char-list {
    background: #1c1c1c;
    font-size: 0;
}

.char-list li {
    display: inline-block;
    border-right: 1px solid #000;
}
.switch-view {
    position: absolute;
    right: 0;
    top: 5px;
    padding-right: 10px;
}
.list-episode-item-2 .title {
    float: left;
    font-weight: normal;
    padding: 5px;
    font-size: 12px;
    color: #899596;
}
.tab li:hover, .tab li.selected {
    background: #FDB813;
margin-left: 0px;
}
}
