css:
@-moz-document domain("rapid.deltion.nl") {
body
{
    min-height: 100%;
    display: flex;
    width: 100vw;
    height: 100vh;
    justify-content: center;
    align-items: center;
    font-family: 'Roboto', sans-serif !important;
}

table.tableDivB th.head {
    background-color: #00a1ff;
    color: #FFF;
}

table.tableDivB td {
    background-color: #99d9ff;
    color: #353535;
}

table.tableDivB td.highlight {
    background-color: #ffffff;
    color: #353535;
}
#hd_right
{
    background: -webkit-gradient(linear, left top, right top, from(white), to(#e0e0ea)) !important;
}

fieldset {
    display: block;
    margin: 2px;
    -moz-border-radius: 8px;
    border: none !important;
}
h1 {
    font-weight: bold;
    font-size: 12px;
    letter-spacing: 2px;
    color: #2063b0;
    margin: 0 0 10px 0;
}

table.tableDivB th.foot {
    background-color: #eeeeee;
    color: #000;
    font-weight: bolder;
}

input[type="submit"], button, a {
    font-family: Roboto,sans-serif;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-size: .875rem;
    line-height: 2.25rem;
    font-weight: 500;
    letter-spacing: .0892857143em;
    text-decoration: none;
    text-transform: uppercase;
    --mdc-ripple-fg-size: 0;
    --mdc-ripple-left: 0;
    --mdc-ripple-top: 0;
    --mdc-ripple-fg-scale: 1;
    --mdc-ripple-fg-translate-end: 0;
    --mdc-ripple-fg-translate-start: 0;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    will-change: transform,opacity;
    padding: 0 8px;
    display: -ms-inline-flexbox;
    display: inline-flex;
    position: relative;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    min-width: 64px;
    height: 36px;
    border: none;
    outline: none;
    line-height: inherit;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-appearance: none;
    overflow: hidden;
    vertical-align: middle;
    border-radius: 4px;
}

input[type="submit"]:hover, button:hover, a:hover {

    font-family: Roboto,sans-serif;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-size: .875rem;
    line-height: 2.25rem;
    font-weight: 500;
    letter-spacing: .0892857143em;
    text-decoration: none;
    text-transform: uppercase;
    --mdc-ripple-fg-size: 0;
    --mdc-ripple-left: 0;
    --mdc-ripple-top: 0;
    --mdc-ripple-fg-scale: 1;
    --mdc-ripple-fg-translate-end: 0;
    --mdc-ripple-fg-translate-start: 0;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    will-change: transform,opacity;
    padding: 0 8px;
    display: -ms-inline-flexbox;
    display: inline-flex;
    position: relative;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    min-width: 64px;
    height: 36px;
    border: none;
    outline: none;
    line-height: inherit;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-appearance: none;
    overflow: hidden;
    vertical-align: middle;
    border-radius: 4px;
    cursor: pointer;
}

input[type=text], input[type=password], input[type=mail], select {
    width: 100%;
    margin: 8px 0;
    display: inline-block;
    border: 1px solid #ccc;
    box-shadow: inset 0 1px 3px #ddd;
    border-radius: 4px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding-left: 20px;
    padding-right: 20px;
    padding-top: 12px;
    padding-bottom: 12px;
}

#hd_right
{
        background: -webkit-gradient(linear, left top, right top, from(#002d66), to(#002d66)) !important;
    color: #fff;
}

#hd_right:last-child
{
    text-transform: uppercase;
    color: #fff;
    font-weight: lighter !important;
}

#hd {
    height: 50px;
    box-shadow: #000000a1 0px 0 20px 1px;
    border-radius: 50px;
}

#menubar_left {
    float: left;
    background-color: transparent !important;
}

#menubar {
    color: white;
    height: 35px;
    line-height: 2.5;
}

.TabbedPanelsContentGroup {
    clear: both;
    border-left: none;
    border-bottom: none;
    border-top: none;
    border-right: none;
    background-color: white;
}

.TabbedPanelsTab {
    position: relative;
    top: 1px;
    float: left;
    padding: 4px 10px;
    margin: 0px 1px 0px 0px;
    /* font: normal 1em sans-serif; */
    /* background-color: #DDD; */
    list-style: none;
    border-left: solid 1px #e0e0e0;
    border-bottom: solid 1px #e0e0e0;
    border-top: solid 1px #e0e0e0;
    border-right: solid 1px #e0e0e0;
    -moz-user-select: none;
    -khtml-user-select: none;
    cursor: pointer;
    border-radius: 5px 5px 0 0;
}

div.present_A {
    background-image: none !important;
    background-color: #36ca36;
}
div.absent_A {
    background-image: none !important;
    background-color: #c10000;
}

div.cel_kop
{
    font-family: Roboto,sans-serif;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-size: 1rem;
    line-height: 1.75rem;
    font-weight: 400;
    letter-spacing: .04em;
    text-decoration: inherit;
    text-transform: inherit;
    background: rgba(60,133,248,.08);
    border-top: none;
    font-size: 1.143rem;
    color: #202124;
    background-image: none !important;
}
div.cel, div.cel_overlap, div.cel_kop {
    position: absolute;
    border: none;
    white-space: nowrap;
    display: block;
}
div#content > div:first-of-type > div:first-of-type {
    float: none !important;
}
}
