css:
@-moz-document domain("duckduckgo.com") {
.cw {
    margin-left:auto;
    margin-right:auto;
}

.header__logo.js-logo-ddg,
.header--aside.js-header-aside,
.js-feedback-btn-wrap,
.badge-link,
.footer {
    display:none !important;
}

.no-touch .zcm__link.is-active, .zcm__link:active, .no-touch .zcm__link.is-active:hover, .no-touch .zcm__link.is-active:hover:active, .no-touch .zcm__link.is-active:focus:active {
	color: #000 !important;
    border-bottom-color: #000;
}

.search__button {
	transition:all 0.15s ease-in;
}

.search--hover .search__button,
.search--focus .search__button{
    background:none !important;
    color:#999 !important;
}

.search__button:hover, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover {
	background-color:#DDD !important;
    color: #777 !important;
}

.result__a,
.result__url,
.result__icon__img,
.result.results_links_deep {
    transition:all 0.175s;
}

/*
.result__url, .result__url:hover, .result__url:active, .result__url:focus {
    color:#777 !important;
}

.result__url:visited {
    color:#AAA !important;
}
*/

.result__a, result__a:hover, result__a:active, result__a:focus {
    color:#00067a;
}

.result__a:visited, result__a:visited:hover, result__a:visited:active, result__a:visited:focus {
    color:#777;
}


.result.results_links_deep .result__icon__img {
	filter: grayscale(100%);
	border-radius: 50%;
}

.result.results_links_deep:hover .result__icon__img {
	filter: grayscale(0%);
	border-radius: 15%;
}

.result.results_links_deep {
    filter:grayscale(0%);
    text-decoration:none !important;
}

.result.results_links_deep:hover {
    filter:grayscale(0%);
}

.result.results_links_deep:hover .result__a {
    text-decoration:none !important;
	color:#084999; /* blue */
}

.result.results_links_deep:hover .result__a:visited {
	color:#6c00a2; /* violet */
}

.result.results_links_deep:hover .result__url,
.result.results_links_deep:hover .result__url:hover,
.result.results_links_deep:hover .result__url:active,
.result.results_links_deep:hover .result__url:focus {
    text-decoration:none !important;
	/*color:#3F6E35 !important;*/
}
}
