css:
@-moz-document domain("last.fm") {
/* --- Profile --- */
/* Misc */
a:link {
    text-decoration: none;
}
.section-control::before {
    border-right: 1px solid #444 !important;
}
.more-link-fullwidth-right>a {
    color: #CCC !important;
}

/* Nav */
.header-metadata-display:hover {
    color:#EEE !important;
}

/* Background */
.adaptive-skin-container, .header, .page-content, .listening-report-promo, #top-tracks::after, .tonefuze::after, .section-with-separator::after  {
    background-color: #333 !important;
}

/* Text */
.header-title, .label, .secondary-nav-item-link, .navlist-more, .header-metadata-display, .chartlist, .shout-body, .shoutbox-heading, .share-chart, .text-colour-link, .stationlink, .listening-report-promo, .hidden-xs, .disclose-trigger, .control-label, .user-dashboard-heading, .user-dashborad-share-heading-desktop, .js-ticker, .duration-data, .share-heading, .wiki-block-inner, .catalogue-metadata, .artist-similar-sidebar, #top-albums, .section-controls, .trending-tracks-section, .features-page-bottom, .more-link-with-action a, .tags-view-all, .about-artist-name{
    color: #CCC !important;
}

/* Links */
.more-link a, .minimal-navigation-item-link:hover, .wiki-block a, .artist-similar-sidebar a, .listeners-section-track a {
    color: #DDD !important;
}

.resource-external-link::before, .resource-external-link::after, .more-link-with-action a::before {
    border-color: #444 !important;
}

.highcharts-plot-band {
    fill: #777 !important;
}

.highcharts-series {
    stroke: none !important;
}
li.tag a {
    box-shadow: inset 0 0 0 1px rgba(221,221,221,.6) !important;
    -webkit-box-shadow: inset 0 0 0 1px rgba(221,221,221,.6) !important;
    color: #DDD !important;
}

.more-link a::before {
    /*background-image: url("/static/images/icons/arrow_small_right_005399_12.cf9465753ea1.png");*/
    /* Blue Chevron */
}

/* Chartlist */
.show-focus-controls {
    background-color: #111 !important;
}
.chartlist-count-bar-slug {
    background-color: #555;
}
a:hover .chartlist-count-bar-slug {
    background-color: #666;
}
.chartlist-row::after{
    border-bottom: 1px solid #444;
}
.chartlist-row--now-scrobbling, .now-scrobbling {
    background-color: #222 !important;
}
.chartlist tr.chartlist-row--even {
    background-color: #333;
}
.chartlist tr, #recent-tracks-section .chartlist--no-index td, .stationlink-list li, #top-tracks-section .chartlist td, .tracklist-section .chartlist--no-index td, #user-loved-tracks-section .chartlist--no-index td, #events-section .events-list td, #user-tags-section .chartlist--no-playlink td { 
    box-shadow:inset 0 -1px #444;
}

/* Station Links */
.stationlink {
    text-decoration: none !important;
}
.stationlink:hover {
    color:#EEE !important;
}
.stationlink::after {
    border-bottom: none !important;
}

/* Shoutbox */
.js-shout-input {
    background-color: #333 !important;
}
.shout-form {
    border-bottom: 1px solid #444;    
}
.link-block-target {
    color: #CCC !important;
}
.shout-permalink:hover, .shout-permalink:focus {
    color: #DDD !important;
}
.shout{
    border-bottom: 1px solid #444;
}

/* Combo Box */
.dropdown-menu-clickable {
    background-color: #333;
}
.dropdown-menu-clickable-item {
    color: #CCC;
}
.dropdown-menu-clickable-item--selected, .dropdown-menu-clickable-item:hover {
    color: #FFF !important;
    /*font-weight: bold;*/
    background-color: #444 !important;
}
.disclose-trigger:hover {
    color: #DDD;
}
.dropdown-menu-clickable::before {
    /*background-image: url("/static/images/dropdown_arrow.47e2426b4e14.png");*/
    /* Arrow for combo box */
}
.section-settings-toggle::before {
    /*background-image: url("/static/images/icons/settings_222_16.1fa6ac1ed6a9.png");*/
}

.share-button-profile::before {
   /* background-image: url("/static/images/icons/share_222_16.9aba752fb1e9.png");*/
}

/* Reports */
.user-dashboard-module {
    background: #444;
    border-color: #222;
    color: #CCC;
}
.user-dashboard-module-description a, .user-dashboard-musical-matches-description a {
    color: #DDD !important;
    text-decoration: none !important;    
}
.user-dashboard-module-description a:hover, .user-dashboard-musical-matches-description a:hover {
    color: #FFF !important;    
}
.highcharts-background {
    fill: #444;
}
.scrobble-stats-graph-chart>div>svg>g>g>path.highcharts-graph {
    stroke: #FFF;
}
.scrobble-stats-graph-chart>div>svg>g>g.highcharts-markers>path {
    fill: #FFF;
}
.highcharts-series {
    stroke: #CCC;    
}
.highcharts-text-outline {
    stroke: #666;
}
.highcharts-label text {
    fill: #FFF !important;
    stroke: #FFF;
    text-outline: 10px !important;
}
.user-dashboard-module-share {
    background: #555;
    box-shadow: 0 0 25px 25px #555;
}
.user-dashboard-module-share-overlay {
    background: #555;
}
.user-dashboard-catalogue-item-total-inner .js-ticker, .user-dashboard-loved-tracks span {
    color: #EEE !important;
    text-shadow: 2px 2px #777;
}
.user-dashboard-footer-cta {
    color: #CCC;
}
.user-dashboard-legend {
    margin-right: 50px !important;
}

/* Library */
.highcharts-axis-labels text {
    fill: #CCC !important;
}
.scrobble-chart>div>div>svg>rect.highcharts-background {
    fill: #333 !important;
}
.highcharts-label text {
    color: #444;
    fill: #444;
    stroke: #AAA;
}
.highcharts-label-box {
    fill:#333;
}

/* Loved Tracks */
.content-top-lower {
    background-color: #333;
}

/* Obsessions */
.obsession-history-play-all {
    color: #CCC;
}

/* Events */
.no-data-message-heading, .metadata-display {
    color: #CCC;    
}
.events-list-item a:hover {
    color: #FFF !important;
}

/* Neighbours */
.user-list-shared-artists a {
    color: #BBB;
}
.user-list-shared-artists a:hover {
    color: #EEE;
}
.user-list-shared-artists {
    border-top-color: #666;
}

/* Tags */
#user-tags-section .chartlist--with-index tr:hover {
    background-color: #222;
}
.countbar-bar-slug {
    background: #555 !important;
}

/* Shouts */
#shoutbox a {
    color: #DDD;
}
#shoutbox a:hover {
    color: #FFF;
}

/* Footer */
.footer {
    color: #CCC;
    /*background-color: #333;*/
}

/* Join Banner */ 
.join-cta {
    background-color: #333;
    border-color: #AAA;
}
.join-cta h3 {
    color: #CCC;
}
.join-cta:hover {
    background-color: #444;
    border-color: #CCC
}
.join-cta-button {
    color: #DDD !important;
}
/* --- Home --- */
.content-top {
    background: #333 !important;
}
.content-top::after{
    border-top: 1px solid #444 !important;    
}

/* --- Music --- */
.content-top-header, .minimal-navigation-item, .music-section-heading, .album-grid-item-artist, .music-track-artist, .music-gallery-artist, .music-recommended-albums-album-artist, .recommended-tracks-item-artist, .big-tags-item-context, .subheader-control-description {
    color: #CCC;
}
.tag:hover, .tags-list:hover, .tag a:hover {
    color: #CCC !important;
    
}
.minimal-navigation-item-link--active, .music-releases-item-artist  {
    color:#FFF;
}
.music-more-link a {
    color: #DDD !important;
}
.link-block-target {
    text-decoration: none !important;
}
.link-block-target:hover {
    color: #EEE !important;
}
.music-recommended-artists-context, .tags-list a, .music-recommended-albums-album-context, .pagination-previous, .pagination-next, .pagination-page {
    color: #666
}

/* --- Charts --- */
.charts-nav-description, .content-top-header, .sidebar-heading{
    color: #FFF;
    font-weight: bold;
}
.charts-nav-item a, .resource-list--sidebar-item-artist, .resource-list--vertical-xs-horizontal-sm-item-artist  {
    color: #CCC;
}
.charts-nav-item a:hover, ._buffer-reset {
    color: #EEE;
}
.globalchart-item {
    background-color: #444;
    box-shadow: 0 1px #666, inset 0 -1px #666 !important;
    -webkit-box-shadow: 0 1px #666, inset 0 -1px #666 !important;
}
.col-sidebar {
    background-color: #333;
}
.two-column-layout, .container {
    background-image: none !important;
}

/* --- Events --- */
.events-list-item-venue--title, .events-list-item-venue--city, .events-list-item-venue--country, .events-list-item-event--lineup {
    color: #CCC;
}
.events-list-item-attendees-count {
    color: #CCC !important;
}
.events-filters{
    border-bottom: 1px solid #444;
}
.events-list-anhv1 td {
    box-shadow: inset 0 -1px #444;
}


/* --- Features --- */
.features-post-body, .features-post-author, .features-post-artist-name {
    color: #CCC;
}
.features-teaser-body {
    background: rgba(255,255,255,0.0) !important;
    background-blend-mode: darken !important;
    padding: 15px !important;
}
.features-teaser-byline {
    color: #AAA;
}
.features-teaser-title a, .features-post-header-title {
    color: #DDD!important;
    font-size: 1.1em;
    text-shadow: 0 1px 0 #CCC, 0 6px 1px rgba(0,0,0,.1), 0 0 5px rgba(0,0,0,.1), 0 1px 3px rgba(0,0,0,.3), 0 3px 5px rgba(0,0,0,.2), 0 5px 10px rgba(0,0,0,.25), 0 10px 10px rgba(0,0,0,.2), 0 20px 20px rgba(0,0,0,.15);
}
.features-post-header-body {
    background:none;
}
.features-post-header-title {
    font-size: 40px;
}
.features-more-link a {
    color: #CCC !important;
}
.features-post-body a {
    color: #DDD ;
    text-decoration: none !important;
}
.features-post-body a:hover {
    color: #FFF ;
}

/* --- Notifications ---*/
.inbox-notifications__item-description {
    color: #CCC;
}
.inbox-notifications__item-link {
    box-shadow: inset 0 -1px #444 !important;    
}
.inbox-notifications__item--hover:hover {
    background-color: #444 !important;
}

/* --- Settings --- */
.form-header, .btn-file-label, .form-group-context, .ignore-list-form, #current-subscription, #subscription-history, .api-sessions, .row h2, .row>.col-main p {
    color: #CCC;
}
.image-upload-remove, .mimic-link, .source-icon-disclaimer a, .form-row-help-text a, #current-subscription a {
    color: #DDD !important;
    text-decoration: none !important;
}
.image-upload-remove:hover, .mimic-link:hover, .source-icon-disclaimer a:hover, .form-row-help-text a:hover, #current-subscription a:hover {
    color: #FFF !important;
}
.responsive-table tr, .responsive-table td {
    box-shadow: inset 0 -1px #444 !important;
    -webkit-box-shadow: inset 0 -1px #444 !important;
}
.responsive-table tr:nth-child(2n) td{
    background-color: #444;
}

/* --- Login --- */
.adaptive-skin-wrapper{
    background: #333 !important;
}
.col-sm-4 h1, .col-sm-offset-4 h1, .col-sm-8 h1, .col-sm-offset-2 h1, .already-joined, .content-form>p {
    color: #CCC;
}
.checkbox a, .already-joined a, .form-submit a, .content-form>p>a {
    color: #DDD !important;
    text-decoration: none !important;
}
.checkbox a:hover, .already-joined a:hover, .form-submit a:hover, .content-form>p>a:hover {
    color: #FFF !important;
}

/* --- Dashboard --- */
.venn_mask {
    /*background-image: url("/static/images/kerve/venn_mask_dark.e0e4fc7f208d.png");*/
}
.widget_label {
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
    margin-bottom: -12px !important;
}

/* --- Error --- */
.row>.col-sm-7, .row>.col-sm-push-4, .error-page-l10n, .checkbox, .form-group-controls {
    color: #CCC;
}
.row>.col-sm-7 a, .row>.col-sm-push-4 a {
    color: #FFF
}
:root .ytp-ad-progress-list, :root #adSkinLeft, :root #adSkinRight, .adblock-message, .adblock-message--benefits {
     display: none !important;
}
}
