css:
@-moz-document domain("infohost.nmt.edu") {
html, body {
    color: #EEC;
    background-color: #1e2428;
    font-family: 'Verdana';
    font-size: 1.05em;
    transition: 0.2s ease;
}
.navheader, .topnavbar {
    background-color: #234;
}
.navheader tr, .navheader td, .navheader th {
    background-color: #234;
    text-align: center;
}
.navheader tr:hover, .navheader td:hover, .navheader th:hover {
    background-color: #234;
}
.navheader img {
    box-sizing: border-box;
    width: 100%;
    height: 100%;
    background-color: #EEC;
}
h1, h2, h3, h4, h5 {
    font-weight: lighter;
}
h1 {font-size: 2.5em; margin: 10px; margin-left: 0px;}
h2 {font-size: 2em; margin: 10px; margin-left: 0px;}
h3 {font-size: 1.6em; margin: 7px; margin-left: 0px;}
h4 {font-size: 1.4em; margin: 5px; margin-left: 0px;}
h5 {font-size: 1.2em; margin: 5px; margin-left: 0px;}
a {
    text-decoration: none;
    color: #ABC;
}
a:hover {
    text-decoration: underline;
}
.programlisting {
    font-size: 1.1em;
    background-color: #000;
    border: 1px solid #777;
    padding: 10px;
    transition: 0.2s ease;
}
.programlisting:hover {
    border: 1px solid #888;
}
table {
    border: 0px;
    border-spacing: 0px;
}
tr, td, th {
    border: 1px solid #777;
    transition: 0.2s ease;
}
td, th {
    padding: 5px;
}
th {
    font-weight: lighter;
    font-size: 1.2em;
}
tr:hover {
    background-color: #2e2e2e;
}
td:hover, th:hover {
    background-color: #444;
}
b {
    font-weight: lighter;
    color: #FF0;
}
.firstterm, .application {
    color: #FAA;
    transition: 0.2s ease;
}
/*.firstterm:hover, .application:hover {
    color: #F77;
}*/
.code {
    color: #AFA;
    transition: 0.2s ease;
}
/*.code:hover {
    color: #7F7;
}*/
}
