css:
@-moz-document domain("minds.com") {
/*BEGIN 2019-04-18*/

/*Private Message Text*/
.mdl-color-text--blue-grey-700 {
    color: #c0c0c0!important;
}

/*Summary text of shared website pages*/
.mdl-color-text--blue-grey-900 {
    color: #c0c0c0!important;
}

/*Blogs*/

/*Publis button*/
body.m-theme__dark .m-button.m-button--text {
    color: #000000;
    border: 1px solid #2f2f2f;
}

/*Groups*/

/*Chat bubble*/
body.m-theme__dark .m-comment__bubble {
    background-color: #404040;
}
/*Text entry bubble*/
body.m-theme__dark .m-comment-wrapper .m-comments-composer form minds-textarea, body.m-theme__dark .m-comment-wrapper .minds-editable-container textarea, body.m-theme__dark m-comments__tree .m-comments-composer form minds-textarea, body.m-theme__dark m-comments__tree .minds-editable-container textarea, body.m-theme__dark minds-comments .m-comments-composer form minds-textarea, body.m-theme__dark minds-comments .minds-editable-container textarea {
    background-color: #040404;
}


/*Top search bubble*/
body.m-theme__dark m-search--bar .mdl-textfield input {
    background-color: #0b0b0b;
}

/*Settings - P2P media - Apply button*/
body.m-theme__dark .m-btn--action[disabled] {
    color: #4690df!important;
    border-color: #4690df!important;
}
/*Personalize your feed*/
/*Switch in the top middle*/
body.m-theme__dark m-switch .m-switch__track {
    border: 1px solid #4690df;
}
/*hashtag text entry*/
body.m-theme__dark input, body.m-theme__dark input:-internal-autofill-previewed, body.m-theme__dark input:-internal-autofill-selected, body.m-theme__dark select:-internal-autofill-previewed, body.m-theme__dark select:-internal-autofill-selected, body.m-theme__dark textarea:-internal-autofill-previewed, body.m-theme__dark textarea:-internal-autofill-selected {
    background-color: #070707;
}
/*Done button*/
body.m-theme__dark .m-btn {
    border: 1px solid #4690df;
    border-top-color: rgb(70, 144, 223);
    border-right-color: rgb(70, 144, 223);
    border-bottom-color: rgb(70, 144, 223);
    border-left-color: rgb(70, 144, 223);
}

/*BEGIN 2019-04-19*/
/*Background padding on images.*/
.m-media-stage {
    background-color: #1b1b1b;
}
}

