css:
@-moz-document domain(root-me.org) {
#main_wrapper {
    background-color: #000;
}
body {
    color: #D2D2D2;
}
.tile {
    background-color: #191c22;
}
#header > nav.top-bar {
    background: #191c22;
}
#header > nav .top-bar-section ul > li > a {
    background: #191c22 !important;
}
#header > nav .top-bar-section ul.left > li > a > img {
    filter: invert(1);
}
table thead tr th, table thead tr td {
    color: #FFF;
}
h1, h2, h3, h4, h5, h6 {
    color: #FFF;
}
table {
    background: #000;
}
table tr:nth-of-type(2n) {
    background: #121419;
}
.noir {
    color:#FFF
}
.tabs dd > a, .tabs .tab-title > a {
    background-color:#121419;
}
.tabs dd > a:hover, .tabs .tab-title > a:hover {
    background-color:#000
}
.tabs dd.active a {
    background-color:#191c22;
    color: #FFF
}
#main dl.tabs > dd.active > a {
    color:#FFF
}
#header > nav .top-bar-section ul > li > a:hover {
    color:#FFF
}
.liste_items li.item:hover {
    background-color: #000;
}
g text {
    fill: #fff;
}
/* LES ADMINS EN VIOLETS !!!! */
 .minirezo {
    color: #8A2BE2 !important;
}
}
