css:
.scheme-gaki-2x {
    background: #333 !important;
}

.wrap {
    background: linear-gradient(to top, #000, #570037);
    border: 2px solid #BC2C5E;
    box-shadow: 0 0 10px #BC2C5E;
}

.lounge-nav {
    background: linear-gradient(to right, #BC2C5E, #793681)
}

.room-name {
    color: #f3f3f3
}

.creator {
    color: magenta;
}

.lounge-top a {
    color: white;
    transition: .2s;
}

.lounge-top a:hover {
    color: magenta !important;
}

.lounge-counter {
    color: white;
}

.btn-default {
    background: transparent;
    color: #f3f3f3;
    transition: .3s;
    border: 2px solid #f3f3f3;
    border-radius: 20px;
    font-weight: bold;
}

.btn-default:hover {
    border-color: #f3f3f3;
}

.progress-bar-label-wrap {
    color: magenta;
}

.rooms-filter-wrap {
    background: #673852 !important;
    border: none;
    border-radius: 2px;
}

.rooms-filter-wrap label {
    color: white !important;
}

#rooms-filter {
    background: #BC2C5E;
    border: none;
    border-radius: 2px;
    color: white;
}

.highlight {
    background: #BC2C5E !important;
    border: none;
    border-radius: 2px;
}

.highlight li {
    color: white;
}

.highlight li a {
    color: white !important;
}

/* i really want this to be global */
