css:
@-moz-document url-prefix("http://campus2.mines-ales.fr") {
body,
#custommenuwrap,
div.breadcrumb,
#custommenu .yui3-menu-label,
#custommenu .yui3-menuitem-content,
#shortsearchbox,
#page-footer,
html,
.navbar,
#frontpage-category-names > h2,
.hasevent,
.contentwithoutlink span {
    border: none;
    background-color: /*[[BASE_COLOR_0]]*/!important;
    color: /*[[TEXT_COLOR]]*/!important;
}

#custommenu .yui3-menu-label-active,
#custommenu .yui3-menuitem-active,
.header,
.course-content .main,
.content,
.content > ul > li,
.no-overflow strong > span,
.submissionsummarytable td,
.fp-navbar,
.filemanager-container,
.custom-select {
    border: none;
    background-color: /*[[BASE_COLOR_1]]*/!important;
    color: /*[[TEXT_COLOR]]*/!important;
}

.yui3-menu-horizontal li {
    border: none;
}

#page-header {
    border-bottom: 4px solid /*[[ACCENT_COLOR]]*/;
    background: /*[[BASE_COLOR_1]]*/;
}

.block .header {
    border-top: 3px solid /*[[ACCENT_COLOR]]*/;
}

#section-0,
.course_category_tree > .content > .subcategories {
    border-top: 4px solid /*[[BASE_COLOR_1]]*/;
}

.course-content,
.course_category_tree > .content,
#inst2 {
    border: none;
    border-top: 3px solid /*[[ACCENT_COLOR]]*/;
    background-color: /*[[BASE_COLOR_1]]*/!important;
    color: /*[[TEXT_COLOR]]*/;
}

#login_password,
input[type=text] {
    border: 1px solid black!important;
    background-color: /*[[BASE_COLOR_0]]*/!important;
    color: /*[[TEXT_COLOR]]*/!important;
    margin-bottom: 5px;
    text-align: center;
}

#inst10 .content {
    padding: 8%;
}

input[type="submit"] {
    border: 1px solid /*[[BASE_COLOR_0]]*/;
    background-color: /*[[ACCENT_COLOR]]*/!important;
    color: /*[[TEXT_COLOR]]*/!important;
    padding: 3px;
}

.login,
.login > a {
    color: black!important;
}

.minicalendar td,
.minicalendar th {
    border-color: #282828;
}

.item-content-wrap,
.instancename,
.title,
h2,
span,
a,
.weekend {
    color: /*[[TEXT_COLOR]]*/!important;
}

.generalbox > .no-overflow {
    visibility: hidden;
}

.dndupload-arrow {
    background: url(https://img.icons8.com/nolan/2x/sort-down.png) center no-repeat;
}
}
