css:
@-moz-document regexp("^(?=.*birdytranslations\\.com)(?=.*spear-master-and-the-black-cat).*$") {
/* Code to Style the Text and Scrollbar */
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee !important;
    font-family: Helvetica, Arial, sans-serif !important;
}

hr {
    border-color: #555555 !important;
}

a {
    color: #c69c6d !important;
}

p {
    color: #eeeeee;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;
}

html {
    scrollbar-color: #555555 #333333;
}

body {
    background-color: #333333;
    border-bottom-color: #333333;
    font-family: Helvetica, Arial, sans-serif;
    line-height: 32px;
}

.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}

.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}

/* Code to center Novel text */
.site {
    max-width: 700px !important;
}

.entry-title {
    margin-top: unset !important;
 	width: unset !important;
    letter-spacing: unset;
}

div[id="content"] {
    width: 700px !important;
    margin: 0 auto !important;
}

div[class="entry-content"] {
 	margin-bottom: unset !important;
}

.entry-wrapper {
 	width: unset !important;   
}

/* Code to remove content from webpage except for the Novels text and navigation. */
 div[id="actionbar"] {
    display: none;
}

header[id="masthead"] {
    display: none;
}

footer[id="colophon"] {
    display: none;
}

div[class="sidebar"] {
    display: none;
}

div[class="entry-meta"] {
    display: none;
}

footer[class="entry-footer"] {
    display: none;
}

div[id="comments"] {
    display: none;
}

div[id="jp-post-flair"] {
    display: none;
}

nav[class="navigation post-navigation"] {
    display: none;
}
}
