css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("streamay.com") {

/* ==== 0- Stream - streamay Widescreen (no Captcha) v.1 (not for serie) - DEPRECATED - 
streamay : https://ww1.streamay.com/ - CAPTCHA DEL - 04.2019
============= */
/* PB with SERIES - 
https://ww1.streamay.com/22668-fargo-2014/
==== */

/* SUPP CAPTCHA (not for serie) - the player work without it */
#Tf-Wp>div[style^="visibility: visible; position: absolute; width: 1908px; top: 1323.9px;"] ,
#Tf-Wp>div[style^="visibility: visible; position: absolute;"] {
    display: none !important;
}

/* SUPP */
.Footer ,
.Container>p {
    display: none !important;
}


/* WIDESCREEN */
#content.Body.site-content .Main.Container {
    max-width: 100% !important;
    padding-top: 0px !important;
}
.AZList {
    margin-top: 76px !important;
}

#content.Body.site-content .Main.Container >section {
    height: 317px !important;
    margin-bottom: -32px !important;
    margin-top: -30px !important;
}
.TpRwCont {
    display: table;
    table-layout: fixed;
    width: 100%;
}
#section-movies>section {
    height: 395px !important;
/* border: 1px solid aqua !important; */
}

/* SERIES - POSSIBLE TO SHOW TEHM but THEY DON'T work well */
#section-series {
    display: inline-block !important;
    height: 416px !important;
    min-width: 100% !important;
    border: 1px solid blue !important;;
}
#section-series>section {
    height: 416px !important;
    min-width: 100% !important;

}



.Top.AAIco-movie_filter ,
.Top.AAIco-star_border {
    margin-bottom: 15px;
}
 main .MovieList.Rows > li ,
#Tf-Wp.page-template-default .Tf-Wp.site  .Body.site-content  main .MovieList.Rows > li {
    -moz-box-flex: 0;
    flex: 0 0 12.5%;
    max-width: 8% !important;
}
.MovieList.Alt .TPost.B .TPMvCn {
    bottom: auto;
    left: 95%;
    min-height: 0;
    min-width: 200px;
    padding-bottom: 0.9rem;
    pointer-events: none;
    right: auto;
    top: 50%;
    transform: translateY(-50%);
    width: 135%;
border: 1px solid blue !important;
}

#Tf-Wp.page-template-default.page #content.Body.site-content .Main.Container ul.AZList + section .MovieListTop.owl-carousel .owl-stage {
    width: 100% !important;
}
#Tf-Wp.page-template-default.page #content.Body.site-content .Main.Container ul.AZList + section .MovieListTop.owl-carousel .owl-stage .owl-item.active {
    margin-right: 10px;
/*     width: 145.417px !important; */
    width: 7.6% !important;
}


/* IN SERIES */
.Top.AAIco-movie_filter ,
.Top.AAIco-movie_filter .Title {
    height: 20px;
    line-height: 15px !important;
    font-size: 15px !important;
}
.RelatedList.owl-carousel.owl-loaded.owl-drag {
    height: 145px !important;
    margin-bottom: 0;
}
.RelatedList.owl-carousel.owl-loaded.owl-drag  .owl-stage-outer {
    height: 145px !important;
    margin-top: -15px !important;
}
.RelatedList.owl-carousel.owl-loaded.owl-drag  .owl-stage-outer .owl-stage {
    height: 135px !important;
/*     margin-top: -15px !important; */
}
.RelatedList.owl-carousel.owl-loaded.owl-drag  .owl-stage-outer .owl-item {
    height: 126px !important;
    top: 5px;
    transform: translate3d(0px, 0px, 0px);
    transition: all 0s ease 0s;
    width: 699px;
}
.RelatedList.owl-carousel.owl-loaded.owl-drag  .owl-stage-outer .owl-item {
    display: inline-block;
    width: 123.625px !important;
    height: 137px !important;
    margin-left: 5px !important;
    margin-right: 4px !important;
/*     border: 1px solid tan; */
}
.RelatedList.owl-carousel.owl-loaded.owl-drag  .owl-stage-outer .owl-item  .TPost.B {
    height: 71px !important;
}






/* PLAYER */
.post-template-default.single.single-post.single-format-standard .TPost.A.D {
float: left !important;
    width: 40% !important;
margin-top: 80px !important;
/* border: 1px solid red !important; */
}
.post-template-default.single.single-post .TPost.A.D + .Main.Container  {
float: right !important;
    margin: 0 auto;
    padding: 0 1rem;
    width: 59% !important;
margin-top: 80px !important;
border: 1px solid gray !important;
}

.post-template-default.single.single-post .TPost.A.D .Container {
    margin-top: -92px !important;
}

.VideoPlayer > span.BtnLight {
    bottom: -420px !important;
    transform: scale(0.5) !important;
}

#content.Body.site-content .Main.Container .TPost.A.Single + section {
    height: 170px !important;
    margin-top: -22px !important;
    border: 1px solid tomato !important;
}
.Top.AAIco-insert_link {
    border-bottom: 1px solid tan;
    height: 20px;
    line-height: 11px;
    margin-bottom: -8px;
}
.MovieList.Rows {
    height: 98px !important;
    padding: 0 !important;
}

#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows {
    height: 80px !important;
    padding: 0 !important;
}
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows .OptionBx {
    height: 29px !important;
    line-height: 20px !important;
    margin-bottom: 0;
    margin-top: 7px !important;
    padding: 0 !important;
}
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows .OptionBx  .Optntl {
    font-size: 15px !important;
    height: 20px !important;
    line-height: 20px !important;
}
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows>li {
display: inline-block !important;
    -moz-box-flex: 0;
/*     flex: 0 0 50% !important; */
max-width: 100% !important;
min-width: 100% !important;
}
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows>li  span.Button.TrLnk ,
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows>li p {
display: inline-block !important;
    -moz-box-flex: 0;
/*     flex: 0 0 50% !important; */
max-width: 20% !important;
min-width: 20% !important;
    margin-bottom: 0  !important;
}
#content.Body.site-content .Main.Container .TPost.A.Single + section .MovieList.Rows>li  span.Button.TrLnk {
vertical-align: middle !important;
    height: 20px !important;
    margin-top: 0px !important;
    padding: 0 !important;
    line-height: 20px !important;
    margin-bottom: 7px  !important;
font-size: 10px !important;
}

/* ==== END  ==== */

}
@-moz-document url-prefix("https://embed.one/videos") {

/* SUPP PLAYER PUBS */

html>body > div {
    display: none !important;
}

/* ==== END  ==== */

}
