css:
@-moz-document domain(audiosciencereview.com) {

body {
    background: #202020;
    color: #dddddd;
}

a {
    color: #e78925;
}

a:hover {
    color: #ffd8ae;
}

.p-pageWrapper {
    background: none !important;
}

.block--messages .message,
.block--messages .block-row {
    color: #dddddd;
    background: none;
}

.message-cell.message-cell--user,
.message-cell.message-cell--action {
    background: #333333;
}

.bbCodeBlock {
    background: #333333;
}

.bbCodeBlock-title {
    background: #444444;
}

.bbCodeBlock-expandLink {
    background: linear-gradient(to bottom, rgba(245, 245, 245, 0) 0%, #444444 80%);
}

.likesBar {

    background: #333333;
}

.notice.notice--primary {
    color: #ececec;
    background: #333333;
}

.p-sectionLinks {
    background: #333333;
    color: #e2913b;
}

.block-container {
    background: #333333;
    color: #dddddd;
}

.block-filterBar {
    background: linear-gradient(0deg, #444444, #141414);
}

.dataList-row.dataList-row--header .dataList-cell {
    background: #444444;
    color: #e2913b;
}

.block-header {
    background: #444444;
    color: #e2913b;
}

.block-footer {
    background: #444444;
}

.block-header .block-desc {
    color: #e2913b;
}

.porta-article-item .porta-article-date .porta-date-block {
    background: #444444;
}

article {
    background: none !important;
}

.structItem-parts > li:nth-child(even) {
    color: #666666;
}

.fr-box.fr-basic.fr-top .fr-wrapper {
    background: none;
}

.fr-box.fr-basic .fr-element {
    color: #dddddd;
}

.tabs--standalone {
    background: #333333;   
}

.formRow>dt {
    background: #494949;
}

.blockLink.is-selected {
    background: #636363;   
}

.blockLink:hover {
    background: #636363 !important;
}

.formSubmitRow-bar {
    background: #636363;   
}

.memberOverviewBlock-seeMore {
    background: #5a5a5a;   
}

.memberHeader-main {
    background: #141414;
}

}
