css:
@-moz-document url-prefix("https://mail.google.com/mail/h/"), url-prefix("https://mail.google.com/mail/u/0/h/") {
/* general */

body {
  background-color: #000;
  color: #fff;
}
a:link, a:active, a:visited,
a.gb1, a.gb4 {
  color: #66c !important;
}
input, select, textarea {
  background-color: #222;
  color: #fff;
}

/* compose */

td[bgcolor="#e0ecff"] {
  background: #124;
}

/* inbox */

table[bgcolor="#C3D9FF"],
td[bgcolor="#C3D9FF"] {
  background: #124;
}
table[bgcolor="#e8eef7"] td[bgcolor="#ffffff"] {
  background: #000;
}
table[bgcolor="#e8eef7"] tr[bgcolor="#ffffff"] {
  background: #222;
}
#bm {
  background: #222;
}

/* message */

table[bgcolor="#e0ecff"] {
  background: #124;
}

table[bgcolor="#cccccc"],
table[bgcolor="#efefef"],
tr[bgcolor="#ffffff"]{
  background: #222;
}

/* sent */

tr[bgcolor="#e8eef7"] {
  background: #222;
}

/* spam */

td[bgcolor="#cccccc"] {
  background: #222;
}

/* trash */

table[bgcolor="#eeeeee"] {
  background: #222;
}

/* contacts */

td[bgcolor="#FAD163"],
table[bgcolor="#fad163"],
table[bgcolor="#fad163"] div.bg {
  background: #530;
}
table[bgcolor="#fad163"] ~ table[bgcolor="#ffffff"] {
  background: #222;
}

/* contact */

td[bgcolor="#fff7d7"] {
  background: #320;
}

/* settings */

.nav ul li.on {
  background: #320;
}

}
