css:
@-moz-document domain("watch-series.cc"), domain("watch-series.co"), domain("watch-series.ru"), domain("watch-series.live"), domain("watchseries.fm"), domain("watchseries.movie")
{html,
body {
    background-color: #000000 !important;
    color: white;
    margin: 0;
    padding: 0;
}
#vh_loading_effect {
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    z-index: 22222;
    background: rgba(0, 0, 0, 0.99);
    opacity: 1;
    -webkit-transition: all .25s ease-out;
    -moz-transition: all .25s ease-out;
    -o-transition: all .25s ease-out;
    -ms-transition: all .25s ease-out;
    transition: all .25s ease-out;
}
.header_search .search .gray-form .footer_search_input {
    font-size: 100%;
    font-family: inherit;
    background-color: #1c1c1c !important;
}
.header_search .search .gray-form .footer_search_input {
    border: none;
    color: #999999;
    background: #90909000;
    float: left;
    margin-left: 70px;
    width: 100%;
    padding-right: 55px;
    line-height: 0;
    text-indent: 12px;
}
.vc_row {
    margin-left: -15px;
    margin-right: -15px;
    background-color: black;
}
.side_menu_seperator {
    width: 1px;
    height: 105%;
    background-color: #0c0c0c;
    position: absolute;
    right: 0px;
    top: -20px;
}
.notify-foot {
    z-index: 99999;
    position: fixed;
    bottom: 0;
    text-align: center;
    width: 100%;
    left: 0;
    padding: 10px;
    background: #1c1c1c;
    color: #9e9e9e;
}
.video-page-container {
    font-family: "Raleway";
    width: 79%;
    float: left;
    padding-right: 32px;
    padding-bottom: 11px;
    color: #db4a3f;
}
.video-page-desc-wrapper:before {
    content: '';
    position: absolute;
    height: 40px;
    width: 100%;
    bottom: 0px;
    z-index: 1;
    background: -moz-linear-gradient(top,rgba(250,250,250,0) 0%,rgba(250,250,250,1) 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(250,250,250,0)),color-stop(100%,rgba(250,250,250,1)));
    background: -webkit-linear-gradient(top,rgba(250,250,250,0) 0%,rgba(250,250,250,1) 100%);
    background: -o-linear-gradient(top,rgba(250,250,250,0) 0%,rgba(250,250,250,1) 100%);
    background: -ms-linear-gradient(top,rgba(250,250,250,0) 0%,rgba(250,250,250,1) 100%);
    background: linear-gradient(to bottom,rgba(250,250,250,0) 0%,rgba(0, 0, 0, 0) 100%);
}
.video-page-desc {
    padding-top: 0;
    border-top: 0;
    margin-top: 0;
    width: 100%;
    float: left;
    clear: none;
    font-size: 15px;
    line-height: 26px;
    color: #ffffff;
    position: relative;
    overflow: hidden;
}
.footer_seperator {
    width: 1px;
    height: 350px;
    background-color: #0c0c0c;
    position: absolute;
    left: 2px;
    bottom: -100px;
}
.footer-container.modern .bottom-menu-container .footer-menu li a {
    display: block;
    color: #fff;
    font-size: 18px;
    -webkit-transition: color 0.2s;
    -moz-transition: color 0.2s;
    -ms-transition: color 0.2s;
    -o-transition: color 0.2s;
    transition: color 0.2s;
}
.wrapper .breadcrumb {
    margin-top: -1px;
    margin-bottom: 15px;
    font-size: 17px;
    color: #828282;
    line-height: 150%;
}
body .wrapper .breadcrumb a {
    font-size: 17px;
    color: #ffffff;
    font-weight: normal;
    line-height: 24px;
}
.tags li a {
    background-color: #1c1c1c;
    display: block;
    padding: 3px 8px;
    font-size: 13px;
    border-radius: 4px;
}
.wpb_row *, .wpb_row *:before, .wpb_row *:after {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
.wpb_btn-info {
    background-color: #db4a3f;
}
.sidebar_menu ul.primary-menu li a {
    padding-right: 40px;
    display: block;
    color: #fff;
    font-size: 15px;
    line-height: 19px;
    font-weight: normal;
    font-family: "Raleway",sans-serif;
}
.sidebar_menu ul.primary-menu li {
    border-bottom: 0px solid #e5e5e5;
}
.anime_muti_link ul li:nth-child(2n+1) {
    background-color: #282828;
}
.anime_muti_link ul li:last-child, .anime_muti_link ul li:first-child {
    border-bottom: solid 1px #db4a3f;
}
.anime_muti_link ul li {
    float: left;
    width: 100%;
    background-color: #000000;
    border-bottom: solid 1px #000;
    position: relative;
}
.anime_muti_link ul li .doamin {
    float: left;
    color: #fff;
    font-weight: 600;
    font-size: 14px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-top: 10px;
}
.anime_muti_link ul li .link {
    float: right;
    color: #fff;
    font-weight: 600;
    font-size: 14px;
    padding-bottom: 10px;
    padding-right: 60px;
    padding-top: 10px;
}
.anime_muti_link ul li a:hover span, .anime_muti_link ul li a.active span {
    background-color: #db4a3f;
}
.anime_muti_link ul li a.active {
    cursor: inherit;
    background-color: #58b9da;
}
.anime_muti_link ul li .server {
    background-position: left center;
    background-repeat: no-repeat;
    padding-left: 30px;
    background-size: 20px auto;
    color: white;
}
.anime_muti_link ul li a.server:hover {
    background: transparent;
}
.anime_muti_link ul li a:hover {
    background: #58b9da;
}
.wrapper .main-inner ol, .wrapper .main-inner ul {
    list-style-position: outside;
    padding-left: 40px;
    margin-bottom: 15px;
    background-color: #28282800;
}

.anime_muti_link ul {
    float: left;
    width: 100%;
    padding: 14px 0 !important;
}
.sidebar_menu ul.primary-menu li.menu-item.micon-anime a {
    background: url(https://i.imgur.com/LUy0mjG.png) right center no-repeat;
}
.wrapper p {
    font-size: 15px;
    color: #d8d8d8;
    margin-bottom: 15px;
    font-family: 'Raleway',sans-serif;
    font-weight: normal;
    line-height: 26px;
}
}
